    // 排序
    sort_change: function sort_change(item) {
      var order = item.order,
          key = item.prop;
      this.filterTableData();
      var newList;

      if (order) {
        switch (order) {
          case "descending":
            if (key == "TM") {
              newList = this.table.Rows_filter.sort(function (a, b) {
                var aTime = new Date("20".concat(a.TM)).getTime(),
                    bTime = new Date("20".concat(b.TM)).getTime();
                aTime = isNaN(aTime) ? 0 : aTime;
                bTime = isNaN(bTime) ? 0 : bTime;
                return bTime - aTime;
              });
            } else if (key == "CS") {
              newList = this.table.Rows_filter.sort(function (a, b) {
                var a_cs = a["CS"] == "正常" ? 1 : 0,
                    b_cs = b["CS"] == "正常" ? 1 : 0;
                return b_cs - a_cs;
              });
            } else {
              newList = this.table.Rows_filter.sort(function (a, b) {
                return Number(b[key]) - Number(a[key]);
              });
            }

            break;

          case "ascending":
            if (key == "TM") {
              newList = this.table.Rows_filter.sort(function (a, b) {
                var aTime = new Date("20".concat(a.TM)).getTime(),
                    bTime = new Date("20".concat(b.TM)).getTime();
                aTime = isNaN(aTime) ? 0 : aTime;
                bTime = isNaN(bTime) ? 0 : bTime;
                return aTime - bTime;
              });
            } else if (key == "CS") {
