                return a_cs - b_cs;
              });
            } else {
              newList = this.table.Rows_filter.sort(function (a, b) {
                return Number(a[key]) - Number(b[key]);
              });
            }

        }

        this.table.Rows_filter = newList;
      }

      this.$TableMethods.refreshCurrentChange(this.table, 1);
    }
