      var MON = date.getMonth() + 1;
      var datearray = new Array();

      if (_parseInt(MON) < 10) {
        MON = "0".concat(MON);
      }

      var Day = date.getDate();

      if (_parseInt(Day) < 10) {
        Day = "0".concat(Day);
      }

      var Hour = date.getHours();

      if (_parseInt(Hour) < 10) {
        Hour = "0".concat(Hour);
      }

      if (date.getHours() < 8) {
        //昨天的时间
        var day1 = new Date();
        day1.setTime(day1.getTime() - 24 * 60 * 60 * 1000);
        day1.setHours(8);
        day1.setMinutes(0);
        day1.setSeconds(0);
        var Hour2 = day1.getHours();
        var MON2 = day1.getMonth() + 1;
        var DAY2 = day1.getDate();

