import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
export default {
  data: function data() {
    return {
      loading: false,
      zdlxlist: [{
        value: '1',
        label: '明渠'
      }, {
        value: '2',
        label: '堰槽'
      }, {
        value: '3',
        label: '渡槽'
      }, {
        value: '4',
        label: '管道'
      }],
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        zdlx: '',
        model_qudao: '',
        date: '',
        searchmsg: '',
        xzqh: '',
        qd: ''
      },
      gjdl: {
        STTP: [],
        Field: ''
      },
      zddj: {
        STGR: [],
        Field: ''
      },
      jcys: {
        TYPE: [],
        Field: ''
      },
      date: '',
      data: [],
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      searchs: ''
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    timechange: function timechange(date) {
      console.log(date);
      this.date = date;
    },
    // 行政区划选择器
    XZQHsearch: function XZQHsearch() {
      console.log(this.form.model_adress);
      this.Reload();
    },
    // 渠道选择器
    QDsearch: function QDsearch() {
      console.log(this.form.model_qudao);
      this.Reload();
    },
    // 国家定类选择器
    STTPUpdate: function STTPUpdate() {
      console.log(this.gjdl.Field);
      this.Reload();
    },
    //站点等级选择器
    STGRUpdate: function STGRUpdate() {
      console.log(this.zddj.Field);
      this.Reload();
    },
    // 监测要素选择器
    jcysUpdate: function jcysUpdate() {
      debugger;
      console.log(this.jcys.Field);
      this.Reload();
    },
    //搜索框方法
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    Reload: function Reload() {
      var _this = this;

      this.loading = true;

      if (this.form.model_adress.length == 0) {
        this.form.xzqh = '';
      }

      if (this.form.model_adress.length == 1) {
        var str1 = this.form.model_adress[0];
        str1 = str1.substring(0, 6);
        this.form.xzqh = str1;
      }

      if (this.form.model_adress.length == 2) {
        var str2 = this.form.model_adress[1];
        str2 = str2.substring(0, 9);
        this.form.xzqh = str2;
      }

      if (this.form.model_adress.length == 3) {
        var str3 = this.form.model_adress[2];
        str3 = str3.substring(0, 12);
        this.form.xzqh = str3;
      }

      if (this.form.model_qudao.length == 0) {
        this.form.qd = '';
      }

      if (this.form.model_qudao.length == 1) {
        var str4 = this.form.model_qudao[0];
        str4 = str4.substring(0, 5);
        this.form.qd = str4;
      }

      if (this.form.model_qudao.length == 2) {
        var str5 = this.form.model_qudao[1];
        str5 = str5.substring(0, 9);
        this.form.qd = str5;
      }

      var obj = {
        ADDVCD: this.form.xzqh,
        STTP: this.gjdl.Field,
        STGR: this.zddj.Field,
        Canal_Code: this.form.qd,
        STNM: this.searchs,
        _orderby: '',
        TYPE: this.jcys.Field
      };
      this.axios.get('/guanqu/jichujiancezhandian/jiancezhandian?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current, {
        params: obj
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.data = res.data.list;
        _this.list_input.total = res.data.total;

        for (var i = 0; i < _this.data.length; i++) {
          if (_this.data[i].DA_Z == '.000') {
            _this.data[i].DA_Z = '-';
          } else {
            _this.data[i].DA_Z = '0' + _this.data[i].DA_Z;
          }

          _this.data[i].DA_Q = '0' + _this.data[i].DA_Q;
        }

        ;
      }); //行政区划

      this.getTableData_WRP_AD_B(function (data) {
        _this.form.adressList = data;
      }); // 获取输排水渠道数据,然后设置渠道选择框选项

      this.getTableData_WRP_IrrBTCanalSystem(function (data) {
        _this.form.qudaoList = data;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=STTP').then(function (res) {
        _this.gjdl.STTP = res.data.list;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=STGR').then(function (res) {
        _this.zddj.STGR = res.data.list;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=TYPE').then(function (res) {
        _this.jcys.TYPE = res.data.list;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  }
};
