import ADDDIALOG from "@/table/table-xtqx/table-jsgl/addDialog.vue";
import EDITDIALOG from "@/table/table-xtqx/table-jsgl/editDialog.vue";
import POWERDIALOG from "@/table/table-xtqx/table-jsgl/powerDialog.vue";
import Utils from '@/assets/utils.js'; // import {constants} from 'crypto';
// import App from "@/App.vue";
// import Table from "@/page/Table.vue"
// import DETAIL from "@/table/table-xtqx/table-cdgl/detail.vue";
// import EDIT from "@/table/table-xtqx/table-cdgl/edit.vue";
//   import NYYSDY from '@/table/table-basedata/table-ysh/nyysdy.vue'
//   import GYYSDY from '@/table/table-basedata/table-ysh/gyysdy.vue'

export default {
  data: function data() {
    return {
      loading: false,
      value: "",
      timeValue: "",
      idList: [],
      item: {},
      list_input: {
        value: "",
        search: "",
        pages: 1,
        page: 1,
        page_size: 10,
        total: 100,
        filter: {}
      },
      dialog_add_showing: false,
      dialog_edit_showing: false,
      dialog_power_showing: false,
      tableData: []
    };
  },
  mounted: function mounted() {
    this.ReLoad();
  },
  methods: {
    ReLoad: function ReLoad() {
      var _this = this;

      this.loading = true;
      this.axios.get('/guanqu/web/sys-roles/page', {
        params: {
          page: this.list_input.page - 1,
          size: this.list_input.page_size,
          name: this.value
        }
      }).then(function (res) {
        _this.tableData = res.data.list;
        _this.list_input.total = res.data.totalCount;
        Utils.$emit('demo', 'msg'); // console.log(this.$Table)

        _this.loading = false;

        _this.tableData.forEach(function (item) {
          var date = new Date(item.ctime);
          var date2 = new Date(item.upTime);
          var year = date.getFullYear();
          var year2 = date2.getFullYear();
          var mon = date.getMonth() + 1;
          var mon2 = date2.getMonth() + 1;

          if (mon < 10) {
            mon = "0".concat(mon);
          }

          if (mon2 < 10) {
            mon2 = "0".concat(mon2);
          }

          var day = date.getDate();
          var day2 = date2.getDate();

          if (day < 10) {
            day = "0".concat(day);
          }

          if (day2 < 10) {
            day2 = "0".concat(day2);
          }

          var hours = date.getHours();
          var hours2 = date2.getHours();

          if (hours < 10) {
            hours = "0".concat(hours);
          }

          if (hours2 < 10) {
            hours2 = "0".concat(hours2);
          }

          var minutes = date.getMinutes();
          var minutes2 = date2.getMinutes();

          if (minutes < 10) {
            minutes = "0".concat(minutes);
          }

          if (minutes2 < 10) {
            minutes2 = "0".concat(minutes2);
          }

          var seconds = date.getSeconds();
          var seconds2 = date2.getSeconds();

          if (seconds < 10) {
            seconds = "0".concat(seconds);
          }

          if (seconds2 < 10) {
            seconds2 = "0".concat(seconds2);
          }

          item.ctime = "".concat(year, "-").concat(mon, "-").concat(day, " ").concat(hours, ":").concat(minutes, ":").concat(seconds);
          item.upTime = "".concat(year2, "-").concat(mon2, "-").concat(day2, " ").concat(hours2, ":").concat(minutes2, ":").concat(seconds2);
        });
      });
    },
    //分页方法
    handleSizeChange: function handleSizeChange(val) {
      console.log(val);
      this.list_input.page = 1;
      this.list_input.page_size = val;
      this.ReLoad();
    },
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.page_size * (this.list_input.page - 1);
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.list_input.page = val;
      this.ReLoad();
    },
    //删除
    del: function del(row) {
      var _this2 = this;

      console.log(row);
      this.$confirm("是否确认删除此角色", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        _this2.axios.delete('/guanqu/web/sys-roles/' + row.id).then(function (res) {
          _this2.$message({
            type: "success",
            message: "删除角色成功!"
          });

          _this2.ReLoad();
        });
      }).catch(function () {// this.$message({
        //   type: "error",
        //   message: "网络错误！删除角色失败！"
        // });
      });
    },
    //批量选中
    selectRow: function selectRow(selection) {
      var _this3 = this;

      // this.idList = selection;
      this.idList = [];
      selection.forEach(function (val, index, array) {
        _this3.idList.push(val.id);
      });
      console.log(this.idList);
    },
    //批量删除
    piliangDel: function piliangDel() {
      var _this4 = this;

      this.$confirm("是否确认批量删除已选用户", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        if (_this4.idList.length == 0) {
          _this4.$message({
            type: "error",
            message: "未选择用户，删除用户失败"
          });
        } else {
          _this4.axios.delete('/guanqu/web/sys-roles/batch-v1', {
            data: _this4.idList
          }).then(function (res) {
            _this4.$message({
              type: "success",
              message: "删除用户成功!"
            });

            _this4.ReLoad();
          });
        }
      }).catch(function () {// this.$message({
        //   type: "error",
        //   message: "网络错误！删除用户失败！"
        // });
      });
    }
  },
  computed: {},
  components: {
    ADDDIALOG: ADDDIALOG,
    EDITDIALOG: EDITDIALOG,
    POWERDIALOG: POWERDIALOG // YSH,
    // NYYSDY,
    // GYYSDY

  }
};
