export default {
  data: function data() {
    return {
      form: {
        name: null,
        url: "",
        index: 1,
        fatherId: [],
        type: "",
        icon: "",
        remark: ""
      },
      menus_list: []
    };
  },
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    Reload: function Reload() {
      var _this = this;

      this.axios.get('/guanqu/web/menus/list').then(function (res) {
        _this.menus_list = res.data;
      });
    },
    Save: function Save() {
      var _this2 = this;

      if (this.form.fatherId.length == 0) {
        this.form.fatherId = null;
      } else {
        this.form.fatherId = this.form.fatherId[this.form.fatherId.length - 1];
      }

      if (!this.form.name) {
        this.$message({
          message: '菜单名称不能为空!',
          type: 'warning'
        });
      } else {
        this.axios.post('/guanqu/web/menus/', {
          name: this.form.name,
          url: this.form.url,
          index: this.form.index,
          fatherId: this.form.fatherId,
          type: this.form.type,
          icon: this.form.icon,
          remark: this.form.remark
        }).then(function (res) {
          _this2.$message({
            message: '添加成功',
            type: 'success'
          });

          _this2.$emit("closewindows");

          _this2.resetForm();
        });
      }
    },
    resetForm: function resetForm() {
      this.form.name = '';
      this.form.url = '';
      this.form.index = 1;
      this.form.fatherId = '';
      this.form.type = '';
      this.form.icon = '';
      this.form.remark = '';
    }
  },
  computed: {},
  components: {}
};
