    Reload: function Reload(agmt) {
      var _this2 = this;

      this.loading = true;

      if (this.form.model_adress.length == 0) {
        this.form.xzqh = '';
      }

      if (this.form.model_adress.length == 1) {
        var str1 = this.form.model_adress[0];
        str1 = str1.substring(0, 6);
        this.form.xzqh = str1;
      }

      if (this.form.model_adress.length == 2) {
        var str2 = this.form.model_adress[1];
        str2 = str2.substring(0, 9);
        this.form.xzqh = str2;
      }

      if (this.form.model_adress.length == 3) {
        var str3 = this.form.model_adress[2];
        str3 = str3.substring(0, 12);
        this.form.xzqh = str3;
      }

      if (this.form.model_qudao.length == 0) {
        this.form.qd = '';
      }

      if (this.form.model_qudao.length == 1) {
        // var str4 = this.form.model_qudao[0];
        // str4 = str4.substring(0, 5);
        this.form.qd = this.form.model_qudao[0];
      }

      if (this.form.model_qudao.length == 2) {
        // var str5 = this.form.model_qudao[1];
        // str5 = str5.substring(0, 9);
        this.form.qd = this.form.model_qudao[1];
      }

      var obj = {
        ADDVCD: this.form.xzqh,
        Canal_Code: this.form.qd,
        STGR: this.form.gsdw,
        LEVEL: this.form.sklx,
        STNM: this.searchs
      };
      this.axios.get('/guanqu/shuiweikurong/lishi_liebiao', {
        params: obj
      }).then(function (res) {
        _this2.loading = false;
        console.log(res);
        _this2.data = res.data.list.map(function (item, index) {
          for (var i in item) {
            switch (i) {
              case 'TM':
                if (item[i] !== null) {
                  item[i] = item[i].substring(0, item[i].length - 2);
                } else {
                  item[i] = '';
                }

                break;

              case 'RZ':
                item[i] = _this2.Z_Filter(item[i], 2);
                break;

              case 'STCP':
                item[i] = _this2.Z_Filter(item[i], 2);
                break;

              default:
                item[i] = item[i];
            }
          }

          console.log(item);
          return item;
        });
        _this2.cols1 = res.data.list;
        _this2.list_input.total = res.data.total; //行政区划

        _this2.getTableData_WRP_AD_B(function (data) {
          _this2.form.adressList = data;
        }); // 获取输排水渠道数据,然后设置渠道选择框选项


        _this2.getTableData_WRP_IrrBTCanalSystem(function (data) {
          _this2.form.qudaoList = data;
        });
      });
