export default {
  props: {
    id: ""
  },
  data: function data() {
    return {
      form: {
        login: "",
        name: "",
        passwd: "",
        confirmPasswd: "",
        roleId: "",
        status: "",
        enterprise: "",
        department: "",
        position: "",
        radio: "",
        birthday: "",
        phone: "",
        remark: ''
      },
      rules: {
        login: [{
          required: true,
          message: "用户名不能为空"
        }],
        name: [{
          required: true,
          message: "姓名不能为空"
        }],
        passwd: [{
          required: true,
          message: "登录密码不能为空"
        }],
        confirmPasswd: [{
          required: true,
          message: "确认密码不能为空"
        }],
        roleId: [{
          required: true,
          message: "所属角色不能为空"
        }],
        status: [{
          required: true,
          message: "状态不能为空",
          trigger: 'change'
        }]
      },
      sexoptions: [{
        label: '男',
        value: 0
      }, {
        label: '女',
        value: 1
      }],
      role: []
    };
  },
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    Reload: function Reload() {
      var _this = this;

      //获取角色
      this.axios.get('/guanqu/web/sys-roles/page?size=99999').then(function (res) {
        _this.role = res.data.list;
      });
    },
    Add: function Add() {
      var _this2 = this;

      if (!this.form.login || !this.form.name || !this.form.passwd || !this.form.confirmPasswd || !this.form.roleId || !this.form.status) {
        this.$message({
          type: "error",
          message: "必填项未输入，新增用户失败"
        });
      } else {
        this.axios.post('/guanqu/web/users/', {
          birthday: this.form.birthday,
          confirmPasswd: this.form.confirmPasswd,
          department: this.form.department,
          enterprise: this.form.enterprise,
          login: this.form.login,
          name: this.form.name,
          passwd: this.form.passwd,
          phone: this.form.phone,
          position: this.form.position,
          remark: this.form.remark,
          roleId: this.form.roleId,
          sex: this.form.sex,
          status: this.form.status
        }).then(function (res) {
          if (res.data.errcode == '400') {
            _this2.$message.error(res.data.message);
          } else {
            _this2.$emit("closewindows");

            _this2.$message({
              type: "success",
              message: "新增用户成功!"
            });
          }
        });
      }
    },
    del: function del() {
      this.$emit("closewindows");
    }
  },
  computed: {},
  watch: {}
};
