export default {
  props: {
    id: ""
  },
  data: function data() {
    return {
      form: {
        newPasswd: "",
        oldPasswd: "" //   rules: {
        //     username: [{ required: true, message: "用户名不能为空" }],
        //     name: [{ required: true, message: "姓名不能为空" }],
        //     newPasswd: [{ required: true, message: "登录密码不能为空" }],
        //     oldPasswd: [{ required: true, message: "确认密码不能为空" }],
        //     role: [{ required: true, message: "所属角色不能为空" }],
        //     stutas: [{ required: true, message: "状态不能为空" }]
        //   }

      }
    };
  },
  mounted: function mounted() {
    console.log(this.id);
  },
  methods: {
    Edit: function Edit() {
      var _this = this;

      if (!this.form.newPasswd || !this.form.oldPasswd) {
        this.$message({
          type: "error",
          message: "新密码和旧密码不能为空!"
        });
      } else if (this.form.newPasswd == this.form.oldPasswd) {
        this.$message({
          type: "error",
          message: "旧密码不能和新密码一样!"
        });
      } else {
        this.axios.put('/guanqu/web/users/' + this.id + '/passwd', {
          newPasswd: this.form.newPasswd,
          oldPasswd: this.form.oldPasswd
        }).then(function (res) {
          if (res.data.errcode == '400') {
            _this.$message.error(res.data.message);
          } else {
            _this.$message({
              type: "success",
              message: "重置密码成功!"
            });

            _this.$emit("closewindows");
          }
        });
      }
    },
    del: function del() {
      this.$emit("closewindows");
    }
  },
  computed: {},
  watch: {}
};
