import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
import App from "@/App.vue";
export default {
  data: function data() {
    return {
      loading: false,
      dialog_detail_showing: false,
      gsdwlist: [{
        value: '1',
        label: '国家重要站'
      }, {
        value: '2',
        label: '省级重要站'
      }, {
        value: '3',
        label: '一般站'
      }, {
        value: '4',
        label: '实验或专用站'
      }, {
        value: '5',
        label: '山洪站'
      }, {
        value: '6',
        label: '临时站'
      }, {
        value: '7',
        label: '其他站'
      }, {
        value: '8',
        label: '灌区站'
      }],
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        zdlx: '',
        model_qudao: '',
        date: '',
        searchmsg: '',
        field: ''
      },
      date: '',
      data: [],
      xzqhdata: [],
      xzqh: '',
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      Tables: {
        title: '',
        total: '',
        data: []
      },
      searchs: '',
      search1: '&STNM=',
      search2: '&TM=',
      search3: '&',
      search4: '&',
      search5: '&',
      timesearch: ''
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    var date = new Date();
    var Month = date.getMonth() + 1;
    this.form.date = date.getFullYear() + '-' + Month;
    this.form.YE = date.getFullYear();
    this.form.MON = Month;
    this.Reload(); // this.Tables.title = date.getFullYear()+'-'+date.getMonth();
  },
  methods: {
    sort_change: function sort_change(column) {
      console.log(column);

      if (column.order == 'ascending') {
        this.form.field = column.prop;
        this.Reload();
      }

      if (column.order == 'descending') {
        this.form.field = column.prop + ' desc';
        this.Reload();
      }

      if (column.order == null) {
        this.form.field = '';
        this.Reload();
      }
    },
    optable: function optable() {
      this.dialog_detail_showing = true;
    },
    cellclick: function cellclick(row, column, cell, event) {
      var evt = new Object();
      evt.graphic = new Object();
      evt.graphic.attributes = row;
      evt.graphic.attributes.itype = "hedaoshuiqing";
      evt.graphic.attributes.rowinfo = JSON.parse(_JSON$stringify(row));
      evt.graphic.attributes.rowinfo.tableType = {
        hdsq: "monthTable"
      };
      var v = new Object();
      v.itype = "hedaoshuiqing";
      console.log("evt", evt);
      this.$App.showDrawer(evt, v);
    },
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    },
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.pagesize * (this.list_input.current - 1);
    },
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = '&STNM=' + this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    timechange: function timechange() {
      console.log(this.form.date);
      this.form.YE = this.form.date.substring(0, 4);
      this.form.MON = this.form.date.substring(6, 7);
      this.Reload();
    },
    XZQHsearch: function XZQHsearch() {
      console.log(this.form.model_adress);
      this.Reload();
    },
    QDsearch: function QDsearch() {
      console.log(this.form.model_qudao);
      this.Reload();
    },
    TYPEsearch: function TYPEsearch() {
      console.log(this.form.zdlx);
      this.Reload();
    },
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      this.loading = false;

      if (this.form.model_adress.length == 0) {
        this.form.xzqh = '';
      }

      if (this.form.model_adress.length == 1) {
        var str1 = this.form.model_adress[0];
        str1 = str1.substring(0, 6);
        this.form.xzqh = str1;
      }

      if (this.form.model_adress.length == 2) {
        var str2 = this.form.model_adress[1];
        str2 = str2.substring(0, 9);
        this.form.xzqh = str2;
      }

      if (this.form.model_adress.length == 3) {
        var str3 = this.form.model_adress[2];
        str3 = str3.substring(0, 12);
        this.form.xzqh = str3;
      }

      if (this.form.model_qudao.length == 0) {
        this.form.qd = '';
      }

      if (this.form.model_qudao.length == 1) {
        // var str4 = this.form.model_qudao[0];
        // str4 = str4.substring(0, 5);
        this.form.qd = this.form.model_qudao[0];
      }

      if (this.form.model_qudao.length == 2) {
        // var str5 = this.form.model_qudao[1];
        // str5 = str5.substring(0, 9);
        this.form.qd = this.form.model_qudao[1];
      }

      this.axios.get('/guanqu/hedaoshuiqing/lishi_yuebiao?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: {
          YR: this.form.YE,
          MON: this.form.MON,
          ADDVCD: this.form.xzqh,
          Canal_Code: this.form.qd,
          CTYPE: '',
          STGR: this.form.zdlx,
          _orderby: ''
        }
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.data = res.data.list; // this.Tables.total = res.data.total;

        _this.list_input.total = res.data.total;
        _this.Tables.title = _this.data[0].YE + '-' + _this.data[0].MON;

        for (var i = 0; i < _this.data.length; i++) {
          _this.data[i].RZ = _this.Z_Filter(_this.data[i].RZ);
          _this.data[i].AWL = _this.Z_Filter(_this.data[i].AWL);
          _this.data[i].Max_RZ = _this.Z_Filter(_this.data[i].Max_RZ);
          _this.data[i].Min_RZ = _this.Z_Filter(_this.data[i].Min_RZ);
          _this.data[i].MWQ = _this.Z_Filter(_this.data[i].MWQ, 3);
          _this.data[i].MA_Q = _this.Z_Filter(_this.data[i].MA_Q, 3);

          if (_this.data[i].CTYPE == '1') {
            _this.data[i].CTYPE = '闸门';
          }

          if (_this.data[i].CTYPE == '2') {
            _this.data[i].CTYPE = '阀门';
          }

          _this.data[i].CV = _this.data[i].CV > 0 ? '↑' : _this.data[i].CV < 0 ? '↓' : _this.data[i].CV == 0 ? '-' : ' ';
        }
      });
      this.axios.get('/guanqu/hedaoshuiqing/lishi_yuebiao?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: {
          YR: this.form.YE,
          MON: this.form.MON,
          ADDVCD: this.form.xzqh,
          Canal_Code: this.form.qd,
          CTYPE: '',
          STGR: this.form.zdlx,
          _orderby: ''
        }
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.Tables.data = res.data.list;

        for (var i = 0; i < _this.Tables.data.length; i++) {
          _this.Tables.data[i].RZ = _this.Z_Filter(_this.Tables.data[i].RZ);
          _this.Tables.data[i].MA_DWZ = _this.Z_Filter(_this.Tables.data[i].MA_DWZ);
          _this.Tables.data[i].MWQ = _this.Z_Filter(_this.Tables.data[i].MWQ, 3);
          _this.Tables.data[i].MA_Q = _this.Z_Filter(_this.Tables.data[i].MA_Q, 3);
        }
      });
      this.axios.get('/guanqu/table/ST_RSVR_R1').then(function (res) {
        _this.Tables.total = res.data.total;
      }); //行政区划

      this.getTableData_WRP_AD_B(function (data) {
        _this.form.adressList = data;
      }); // 获取输排水渠道数据,然后设置渠道选择框选项

      this.getTableData_WRP_IrrBTCanalSystem(function (data) {
        _this.form.qudaoList = data;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.data[0].ID = index * this.list_input.pagesize;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  },
  components: {},
  created: function created() {
    console.dir("showDrawer", this.$showDrawer);
  }
};
