import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
export default {
  data: function data() {
    return {
      getToday: '',
      loading: false,
      activeNames: [],
      list_input: {
        total: 100,
        pagesize: 48,
        pagesizeopts: [10, 20, 48, 75, 100, 200],
        current: 1
      },
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        zdlx: '',
        model_qudao: '',
        date: '',
        searchmsg: '',
        xzqh: '',
        qd: ''
      },
      timesearch: '',
      data: [],
      searchs: '',
      STNMSlength: 0,
      STNMlength: 0,
      STinfo: {
        CZMZ: ''
      }
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    var _this = this;

    this.axios.get("/guanqu/table/qudao_tree?type=4").then(function (res) {
      // this.Treedata = this.QuDao_Tree_Filter(res.data).data;
      _this.STinfo.CZMC = _this.QuDao_Tree_Filter(res.data).siteinfo.canal_name;
      console.log(_this.QuDao_Tree_Filter(res.data)); // this.searchs = `&STCD=${this.QuDao_Tree_Filter(res.data).siteinfo.canal_code}`;

      _this.STinfo.STCD = _this.QuDao_Tree_Filter(res.data).siteinfo.canal_code;

      _this.Reload();

      console.log('渠道', _this.QuDao_Tree_Filter(res.data));

      _this.Reload();
    });
    var date = new Date();
    var Month = date.getMonth() + 1;

    if (Month < 10) {
      Month = '0' + Month;
    }

    var Hours = date.getHours() - 1;
    var TM = new Array();
    TM[0] = date.getFullYear() + '-' + Month + '-' + date.getDate() + ' ' + Hours + ':00:00.000';
    TM[1] = date.getFullYear() + '-' + Month + '-' + date.getDate() + ' ' + date.getHours() + ':00:00.000';
    console.log(TM);
    this.form.date = TM;
  },
  methods: {
    handleChange: function handleChange(val) {
      console.log(val);
    },
    fangda: function fangda(data, index) {
      data = data.map(function (val) {
        val.url = val.Image;
        return val;
      }); // console.log(item);

      this.$App.enlarge_img(data, index);
    },
    cellclick: function cellclick(val) {
      console.log(val);
      var evt = new Object();
      evt.graphic = new Object();
      evt.graphic.attributes = val;
      evt.graphic.attributes.STNM = val.STNM;
      evt.graphic.attributes.itype = "tuxiang";
      evt.graphic.attributes.rowinfo = JSON.parse(_JSON$stringify(val));
      evt.graphic.attributes.rowinfo.tableType = {
        tx: "historyTable"
      };
      var v = new Object();
      v.itype = "tuxiang";
      console.log("evt", evt);
      this.$App.showDrawer(evt, v);
    },
    search: function search() {
      if (this.form.searchmsg == '') {
        this.searchs = '';
      } else {
        this.searchs = this.form.searchmsg;
      }

      this.Reload();
    },
    XZQHsearch: function XZQHsearch() {
      console.log(this.form.model_adress);
      this.Reload();
    },
    QDsearch: function QDsearch() {
      console.log(this.form.model_qudao);
      this.Reload();
    },
    Reload: function Reload() {
      var _this2 = this;

      this.loading = true;

      if (this.form.model_adress.length == 0) {
        this.form.xzqh = '';
      }

      if (this.form.model_adress.length == 1) {
        var str1 = this.form.model_adress[0];
        str1 = str1.substring(0, 6);
        this.form.xzqh = str1;
      }

      if (this.form.model_adress.length == 2) {
        var str2 = this.form.model_adress[1];
        str2 = str2.substring(0, 9);
        this.form.xzqh = str2;
      }

      if (this.form.model_adress.length == 3) {
        var str3 = this.form.model_adress[2];
        str3 = str3.substring(0, 12);
        this.form.xzqh = str3;
      }

      if (this.form.model_qudao.length == 0) {
        this.form.qd = '';
      }

      if (this.form.model_qudao.length == 1) {
        // var str4 = this.form.model_qudao[0];
        // str4 = str4.substring(0,5);
        this.form.qd = this.form.model_qudao[0];
      }

      if (this.form.model_qudao.length == 2) {
        // var str5 = this.form.model_qudao[1];
        // str5 = str5.substring(0,9);
        this.form.qd = this.form.model_qudao[1];
      } // this.axios.get('/guanqu/table/ST_JPG_H?_page_size='+this.list_input.pagesize+'&_page='+this.list_input.current+this.searchs,{params:{ADDVCD:this.form.xzqh,Canal_Code:this.form.qd}}).then((res)=>{
      //     this.loading = false;
      // });


      this.axios.get('/guanqu/tuxiangxinxi/lishi_tuxiang?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current, {
        params: {
          ADDVCD: this.form.xzqh,
          STNM: this.searchs,
          Canal_Code: this.form.qd
        }
      }).then(function (res) {
        _this2.loading = false;
        _this2.data = res.data.list;
        console.log(_this2.data);
        _this2.list_input.total = res.data.total;

        for (var i = 0; i < _this2.data.length; i++) {
          _this2.data[i].TM = _this2.data[i].TM.replace('.000', '').replace('20', '');
        }

        _this2.data.forEach(function (val, index, array) {
          var Image = '/guanqu/gis/pic_content?id=' + val.STCD;

          _this2.axios.get('/guanqu/tuxiang/details?STCD=' + val.STCD).then(function (res) {
            if (res.data.GUANLIAN_YAOSU.length != 0) {
              val.GUANLIAN_YAOSU = res.data.GUANLIAN_YAOSU;
            }
          });

          val.Image = Image;
        });

        console.log('关联要素', _this2.data);
      });
      this.axios.get('/guanqu/tuxiangxinxi/lishi_tuxiang?_page_size=99999', {
        params: {
          ADDVCD: this.form.xzqh,
          Canal_Code: this.form.qd
        }
      }).then(function (res) {
        _this2.STNMSlength = res.data.total;
      });
      this.getTableData_WRP_AD_B(function (data) {
        _this2.form.adressList = data;
      }); // 获取输排水渠道数据,然后设置渠道选择框选项

      this.getTableData_WRP_IrrBTCanalSystem(function (data) {
        _this2.form.qudaoList = data;
      });
      this.axios.get('/guanqu/tuxiangxinxi/lishi_tuxiangtj').then(function (res) {
        console.log(res);
        _this2.getToday = res.data;
      });
    },
    err: function err() {
      this.$Message.warning('该功能还在开发中...');
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  }
};
