export default {
  props: {
    id: ""
  },
  data: function data() {
    return {
      form: {
        name: "",
        grade: "",
        description: "",
        remark: ""
      },
      rules: {
        name: [{
          required: true,
          trigger: "blur",
          message: "不能为空"
        }]
      }
    };
  },
  mounted: function mounted() {
    console.log(this.id);
    this.Reload();
  },
  methods: {
    Reload: function Reload() {
      var _this = this;

      this.axios.get('/guanqu/web/sys-roles/' + this.id + '/vo').then(function (res) {
        _this.form = res.data;
      });
    },
    EDIT: function EDIT() {
      var _this2 = this;

      this.$confirm("是否确认修改内容", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        if (!_this2.form.name) {
          _this2.$message({
            type: "error",
            message: "必填项未输入，修改用户失败"
          });
        } else {
          _this2.axios.put('/guanqu/web/sys-roles/', {
            id: _this2.id,
            name: _this2.form.name,
            grade: _this2.form.grade,
            description: _this2.form.description,
            remark: _this2.form.remark
          }).then(function (res) {
            _this2.$message({
              type: "success",
              message: "修改用户成功!"
            });

            _this2.$emit("closewindows");

            _this2.Reload();
          });
        }
      }).catch(function () {// this.$message({
        //   type: "error",
        //   message: "网络错误！修改用户失败！"
        // });
      });
    },
    del: function del() {
      this.$emit("closewindows");
    }
  },
  computed: {},
  watch: {}
};
