    // 加载底图
    loadSHP: function loadSHP() {
      var _this4 = this;

      this.axios.get("/static/demo-hunanguanqu/ditu.json").then(function (res) {
        var data = res.data,
            list = data.shpList;
        console.warn("res", res);
        console.warn("list", list);
        if (list.length <= 0) return;
        list.forEach(function (val, index) {
          var data = val.data;
          var features = data.features;
          var color = val.color;
          var size = val.size || 2;
          console.warn("data", data);
          var graphicsSHPlayer = new esri.layers.GraphicsLayer();

          _this4.map.addLayer(graphicsSHPlayer);

          for (var j = 0; j < features.length; j++) {
            var feature = features[j];
            var graphic = null;

            switch (feature.geometry.type) {
              //点
              case "Point":
                //symbol = new esri.symbol.PictureMarkerSymbol(getRootPath() + "images/point1.png", 22, 27);
                symbol = new esri.symbol.SimpleMarkerSymbol({
                  size: size,
                  style: "simplefillsymbol"
                });
                symbol.setColor(color);
                var mappoint = new esri.geometry.Point(feature.geometry.coordinates[0], feature.geometry.coordinates[1], new esri.SpatialReference({
                  wkid: 4326
                }));
                graphic = new esri.Graphic(mappoint, symbol);
                var mappoint_text = new esri.geometry.Point(feature.geometry.coordinates[0], feature.geometry.coordinates[1], new esri.SpatialReference({
                  wkid: 4326
                }));
                var textsymbol = new esri.symbol.TextSymbol(feature.properties.NM);
                textsymbol.setFont(new esri.symbol.Font("8pt").setWeight(esri.symbol.Font.WEIGHT_BOLD).setFamily("Arial"));
                textsymbol.setOffset(0, 30);
                textsymbol.setColor(new esri.Color([0, 0, 0]));
                var graphic_text = new esri.Graphic(mappoint_text, textsymbol);
                if (graphic_text) graphicsSHPlayer.add(graphic_text);
                break;
              //线

              case "LineString":
                symbol = new esri.symbol.SimpleLineSymbol(esri.symbol.SimpleLineSymbol.STYLE_SOLID, new dojo.Color(color), size);
                var polyline = new esri.geometry.Polyline(feature.geometry.coordinates);
                graphic = new esri.Graphic(polyline, symbol);
                break;
              //线

              case "MultiLineString":
                var symbol = new esri.symbol.SimpleLineSymbol(esri.symbol.SimpleLineSymbol.STYLE_SOLID, new dojo.Color(color), size);
                var polyline = new esri.geometry.Polyline(feature.geometry.coordinates);
                var graphic = new esri.Graphic(polyline, symbol);
                break;
              //面

              case "Polygon":
                symbol = new esri.symbol.SimpleFillSymbol(esri.symbol.SimpleFillSymbol.STYLE_SOLID, new esri.symbol.SimpleLineSymbol(esri.symbol.SimpleLineSymbol.STYLE_SOLID, new esri.Color([211, 211, 211]), 1), new esri.Color(color));
                var polygon = new esri.geometry.Polygon(feature.geometry.coordinates);
                graphic = new esri.Graphic(polygon, symbol);
                break;
            }

            graphicsSHPlayer.add(graphic);
          }

          console.log("graphicsSHPlayer", graphicsSHPlayer);

          if (val.name == "界限") {
            val.name = "乡镇";
          }

          if (val.name == "界限2") {
            val.name = "村";
          }

          if (val.name == "渠道") {
            val.name = "河流";
          }

          if (val.name == "渠道名称") {
            val.name = "水系";
          }

          if (val.name == "水库") {
            val.name = "界限";
          }

          _this4.tu_ceng_list.push({
            name: val.name,
            show: val.show,
            layer: graphicsSHPlayer,
            icon: val.icon,
            icon_active: val.icon_active
          });

          if (val.show == false) {
            graphicsSHPlayer.hide();
          } else {
            graphicsSHPlayer.show();
          }
        });

        _this4.getAll();
      });
    },
