    // 获取具有过滤条件的图层数据
    getFeatrueLayer: function getFeatrueLayer(fields, callback, errCallback) {
      /*
                type为数字，以下为不同数字对应图层
                4: 渠道水情
                5: 闸阀水情
                6: 闸阀状态
                7: 图像
                8: 视频
                */
      var url = "/guanqu/sksiteinfo/gis";
      var body = {// _filter: new Object()
      }; // 增加过滤字段

      for (var key in fields) {
        body[key] = fields[key];
      }

      this.axios.post(url, body).then(function (res) {
        if (typeof errCallback == "function") {
          errCallback();
        }

        if (typeof callback == "function") {
          callback(res);
        }
      });
