    clockwise_rotation: function clockwise_rotation() {
      var ele = document.getElementById("img".concat(this.enlargeModal_carousel.initial_index));
      var rotate = ele.style.transform; // console.log("index",this.enlargeModal_carousel.initial_index);
      // console.log("ele",ele);

      if (rotate == "") {
        ele.style.transform = "rotate(90deg)";
      } else {
        rotate = _parseInt(rotate.match(/\d+/g));
        ele.style.transform = "rotate(".concat(rotate + 90, "deg)");
      } // .match(/\d+/g)

    },
    // 图片逆时针旋转
    counterclockwise_rotation: function counterclockwise_rotation() {
      var ele = document.getElementById("img".concat(this.enlargeModal_carousel.initial_index));
      var rotate = ele.style.transform;

      if (rotate == "") {
        ele.style.transform = "rotate(-90deg)";
      } else {
        rotate = _parseInt(rotate.match(/\d+/g));
        ele.style.transform = "rotate(".concat(rotate - 90, "deg)");
      }
    },
