    //获取默认时间
    getinittime: function getinittime() {
      var date = new Date();
      var YR = date.getFullYear();
      var MON = date.getMonth() + 1;
      var datearray = new Array();

      if (_parseInt(MON) < 10) {
        MON = "0".concat(MON);
      }

      var Day = date.getDate();

      if (_parseInt(Day) < 10) {
        Day = "0".concat(Day);
      }

      var Hour = date.getHours();

      if (_parseInt(Hour) < 10) {
        Hour = "0".concat(Hour);
      }

      if (date.getHours() < 8) {
        //昨天的时间
        var day1 = new Date();
        day1.setTime(day1.getTime() - 24 * 60 * 60 * 1000);
        day1.setHours(8);
        day1.setMinutes(0);
        day1.setSeconds(0);
        var Hour2 = day1.getHours();
        var MON2 = day1.getMonth() + 1;
        var DAY2 = day1.getDate();

        if (_parseInt(Hour2) < 10) {
          Hour2 = "0".concat(Hour2);
        }

        if (_parseInt(MON2) < 10) {
          MON2 = "0".concat(MON2);
        }

        if (_parseInt(DAY2) < 10) {
          DAY2 = "0".concat(DAY2);
        }

        var s1 = "".concat(day1.getFullYear(), "-").concat(MON2, "-").concat(DAY2, " ").concat(Hour2, ":00:00");
        datearray[0] = s1;
        datearray[1] = "".concat(YR, "-").concat(MON, "-").concat(Day, " ").concat(date.getHours(), ":00:00");
        console.log('time1', s1, 'time2', datearray[1]);
      } else {
        datearray[0] = "".concat(YR, "-").concat(MON, "-").concat(Day, " 08:00:00");
        datearray[1] = "".concat(YR, "-").concat(MON, "-").concat(Day, " ").concat(Hour, ":00:00");
      }

      return datearray;
    },
