    strlen: function strlen(str) {
      var len = 0;

      for (var i = 0; i < str.length; i++) {
        var c = str.charCodeAt(i); //单字节加1

        if (c >= 255) {
          len += 2;
        } else {
          //汉字加2
          len += 1;
        }
      }

      return len;
    },
