    showShipinTips: function showShipinTips(item) {
      console.log('悬浮框');
      console.log(item);
      var imgSrc = ''; // if(item.rowinfo.picPath !== null){
      //   imgSrc = item.rowinfo.picPath.replace('r','')
      // }

      console.log("/guanqu/pic".concat(imgSrc));
      var content = "<div>\n        <div style=\"height: 20px\"></div>\n        <div class=\"video\">\n          <img src=\"http://222.240.238.58:7001/pic".concat(imgSrc, "\"   width=\"210\" height=\"160\"/>\n        </div>\n      </div>");
      this.map.infoWindow.resize(250, 650); // 提示框大小

      this.map.infoWindow.setTitle(item.STNM); // 提示题目

      this.map.infoWindow.setContent(content); // 提示内容
      //获取坐标

      var x = Number(item.LGTD);
      var y = Number(item.LTTD); //如果basemap使用ArcGIS的就需要
      //转换坐标系

      x = x / 180.0 * 20037508.34;
      if (y > 85.05112) y = 85.05112;
      if (y < -85.05112) y = -85.05112;
      y = Math.PI / 180.0 * y;
      var tmp = Math.PI / 4.0 + y / 2.0;
      y = 20037508.34 * Math.log(Math.tan(tmp)) / Math.PI; //位置点对象

      var zoompoint = {
        //spatialReference: {wkid: 4326},
        spatialReference: this.map.spatialReference,
        x: x,
        y: y
      };
      this.map.infoWindow.show(zoompoint); // 提示位置
