        if (warmType == 2) {
          if (val.rowinfo.status == 0) {
            return true;
          }
        }

        if (warmType == 3) {
          if (val.rowinfo.gkstatus == 0) {
            return true;
          }
        }
      });
      data.features = AbnormalData;
      return data;
