    // 预警通知
    notify_Layer: function notify_Layer(options, data) {
      var _this17 = this;

      if (data.length > 0) {
        if (this.radio4 == "全部") {
          this.Message_Window_show(true); // 打开消息窗口
        }

        var Voice_Controller = this.Message_Window.Voice_Controller,
            notify_info_list = this.Message_Window.notify_info.list;
        var time = 0;

        if (this.SiteFeatrueLayer.checknum != options.checkindex) {
          notify_info_list.length = 0;
          this.SiteFeatrueLayer.checknum = options.checkindex;
        }

        data.forEach(function (val, index, array) {
          var rowinfo = val.rowinfo;
          var item = new Object();
          item.icon = options.icon;

          if (options.sign != "safe") {
            if (options.sign == "sw") {
              item.text = "".concat(rowinfo.STNM) + "水位超汛限";
              item.color = "#FF0000";
            } else if (options.sign == "yl") {
              var raintext = _this17.showRainWarm(rowinfo);

              if (raintext == "无") {
                item.text = "".concat(rowinfo.STNM) + "雨量异常";
              } else {
                item.text = "".concat(rowinfo.STNM) + "雨量" + raintext;
              }

              item.color = "#FFFF00";
            } else {
              item.text = "".concat(rowinfo.STNM);
              item.color = "#5500FF";

              if (rowinfo.vol < rowinfo.VOLMIN || rowinfo.vol > rowinfo.VOLMAX) {
                item.text += "电压异常";
              }

              if (rowinfo.CS == "异常") {
                item.text += "通讯异常";
              }
            }
          } else {
            item.text = "".concat(rowinfo.STNM);

            if (rowinfo.sllyj == "1") {
              item.text += "渗流量、";
            }

            if (rowinfo.slylyj == "1") {
              item.text += "渗压水位、";
            }

            if (rowinfo.spwyyj == "1") {
              item.text += "位移变形、";
            }

            if (rowinfo.czwyyj == "1") {
              item.text += "沉降变形、";
            }

            item.text = item.text.substring(0, item.text.length - 1);
            item.text += "预警";
          }

          item.voice = _this17.baiduVoice(item.text);
          item.voice_duration = item.text.length * 300;
          item.id = notify_info_list.length;
          notify_info_list.unshift(item);
          setTimeout(function () {
            if (Voice_Controller) {
              var duration = notify_info_list[index].voice.duration;
              time += duration * 1000;
              var copyTime = JSON.parse(_JSON$stringify(time));
              setTimeout(function () {
                var voice = _this17.Message_Window.notify_info.list[index].voice;

                if (voice) {
                  voice.play();
                }
              }, copyTime);
            }
          }, 500);
        });
      }
    },
    //雨量预警等级
    showRainWarm: function showRainWarm(rowinfo) {
      var text = "";
      var level = 0;

      if (level < rowinfo.SIGN1) {
        level = rowinfo.SIGN1;
      }

      if (level < rowinfo.SIGN3) {
        level = rowinfo.SIGN3;
      }

      if (level < rowinfo.SIGN6) {
        level = rowinfo.SIGN6;
      }

      if (level < rowinfo.SIGN12) {
        level = rowinfo.SIGN12;
      }

      if (level < rowinfo.SIGN24) {
        level = rowinfo.SIGN24;
      }

      if (level == 0) {
        text = "无";
      }

      if (level == 1) {
        text = "黄色预警";
      }

      if (level == 2) {
        text = "橙色预警";
      }

      if (level == 3) {
        text = "红色预警";
      }

      return text;
    },
