    // 移除指定图层
    removeFeatureLayer: function removeFeatureLayer(featureLayerOBJ) {
      // 从地图中移除图层
      if (featureLayerOBJ.LayerObject) {
        this.map.removeLayer(featureLayerOBJ.LayerObject);
      } // 从控制面板的图层对象中清空图层及图层特征点数据


      featureLayerOBJ.LayerObject = null;
      featureLayerOBJ.Rows = [];
    },
    // 移除指定图层的预警图层
    removeFeatureLayer_yujing: function removeFeatureLayer_yujing(featureLayerOBJ) {
      // 从地图中移除图层
      if (featureLayerOBJ.LayerObject_yujing) {
        for (var i = 0; i < featureLayerOBJ.LayerObject_yujing.length; i++) {
          this.map.removeLayer(featureLayerOBJ.LayerObject_yujing[i]);
        }
      } // 从控制面板的图层对象中清空图层及图层特征点数据


      featureLayerOBJ.LayerObject_yujing = null; // featureLayerOBJ.Rows = [];
    },
    // 创建图层（根据图层数据）
    createFeatrueLayer: function createFeatrueLayer(graphicsData) {
      var data = JSON.parse(_JSON$stringify(graphicsData)); // 图层数据深拷贝
      // 把每个特征点的rowinfo数据放入每个特征点的属性中

      data.features.forEach(function (val, index) {
        data.features[index].attributes.rowinfo = data.features[index].rowinfo;
      }); //构建图层

      var featureCollection = {
        layerDefinition: data,
        featureSet: new esri.tasks.FeatureSet(data)
      };
      var featureLayer = new esri.layers.FeatureLayer(featureCollection);
      return featureLayer;
    },
    // 设置图层的特征点图标
    setFeatrueLayerSymbol: function setFeatrueLayerSymbol(featureLayer, iconUrl, option) {
      var symbol = new esri.symbol.PictureMarkerSymbol(iconUrl, option ? option.width : 16, option ? option.height : 20);
      var renderer = new esri.renderer.SimpleRenderer(symbol);
      featureLayer.setRenderer(renderer);
    },
    // 添加图层到地图
    addFeatrueLayerToMap: function addFeatrueLayerToMap(featureLayer) {
      this.map.addLayer(featureLayer);
    },
    // 添加图层对象到控制面板
    addFeatrueLayerObjToBaseBox: function addFeatrueLayerObjToBaseBox(featureLayerOBJ) {
      // 排序
      switch (featureLayerOBJ.title) {
        case "雨情":
          this.FeatrueLayers.splice(3, 0, featureLayerOBJ);
          break;

        case "水库水情":
          this.FeatrueLayers.splice(4, 0, featureLayerOBJ);
          break;

        case "河道水情":
          this.FeatrueLayers.splice(6, 0, featureLayerOBJ);
          break;

        case "渠道水情":
          this.FeatrueLayers.splice(0, 0, featureLayerOBJ);
          break;

        case "闸阀水情":
          this.FeatrueLayers.splice(1, 0, featureLayerOBJ);
          break;

        case "闸阀状态":
          this.FeatrueLayers.splice(5, 0, featureLayerOBJ);
          break;

        case "图像":
          this.FeatrueLayers.splice(2, 0, featureLayerOBJ);
          break;

        case "视频":
          this.FeatrueLayers.splice(7, 0, featureLayerOBJ);
          break;

        case "运行工况":
          this.FeatrueLayers.splice(8, 0, featureLayerOBJ);
          break;
      }
    },
