    updateFeatrueLayers: function updateFeatrueLayers(featureLayerOBJ, featureLayer, featrueLayer_yujing, data) {
      var _this24 = this;

      featureLayerOBJ.LayerObject = featureLayer; // 添加图层到图层对象

      featureLayerOBJ.LayerObject_yujing = featrueLayer_yujing; // 添加图层到图层对象

      featureLayerOBJ.Rows = data.features; // 添加图层特征点数据到图层对象
      // 更新FeatrueLayers

      this.FeatrueLayers.forEach(function (val, index) {
        if (val.itype == featureLayerOBJ.itype) {
          _this24.FeatrueLayers.splice(index, 1, featureLayerOBJ);
        }
      });
      return featureLayerOBJ;
