import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
import App from "@/App.vue";
export default {
  data: function data() {
    return {
      gsdwlist: [{
        value: '1',
        label: '国家重要站'
      }, {
        value: '2',
        label: '省级重要站'
      }, {
        value: '3',
        label: '一般站'
      }, {
        value: '4',
        label: '实验或专用站'
      }, {
        value: '5',
        label: '山洪站'
      }, {
        value: '6',
        label: '临时站'
      }, {
        value: '7',
        label: '其他站'
      }, {
        value: '8',
        label: '灌区站'
      }],
      skdjlist: [{
        value: '1',
        label: '大型'
      }, {
        value: '2',
        label: '中型'
      }, {
        value: '3',
        label: '小I型'
      }, {
        value: '4',
        label: '小II型'
      }, {
        value: '5',
        label: '山塘'
      }],
      cols1: [],
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        gsdw: '',
        skdj: '',
        model_qudao: '',
        date: [],
        searchmsg: '',
        xzqh: '',
        qd: '',
        field: ''
      },
      date: '',
      xzqhdata: [],
      xzqh: '',
      loading: false,
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      data: [],
      searchs: '',
      timesearch: '',
      STinfo: {
        STCD: ''
      }
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    var date = new Date();
    var Month = date.getMonth() + 1;
    var datelist = new Array();
    var Hours = date.getHours();
    var Minutes = date.getMinutes();
    var Seconds = date.getSeconds();

    if (Hours < 10) {
      Hours = "0".concat(Hours);
    }

    if (Minutes < 10) {
      Minutes = "0".concat(Minutes);
    }

    if (Seconds < 10) {
      Seconds = "0".concat(Seconds);
    }

    if (Month < 10) {
      Month = "0".concat(Month);
    }

    datelist[0] = "".concat(date.getFullYear(), "-").concat(Month, "-").concat(date.getDate(), " 00:00:00");
    datelist[1] = "".concat(date.getFullYear(), "-").concat(Month, "-").concat(date.getDate(), " ").concat(Hours, ":").concat(Minutes, ":").concat(Seconds);
    this.form.date = datelist;
    console.log(this.form.date);
    this.Reload();
  },
  methods: {
    cellclick: function cellclick(row, column, cell, event) {
      var _this = this;

      // console.log(column.property);
      var value = column.property.replace('平均水位', '').replace('水势', '').replace('平均出库流量', '').replace('平均入库流量', '').replace('小时蓄水量', '');
      console.log(this.cols1);
      this.cols1.forEach(function (val, index, array) {
        if (val.STNM == value) {
          var STCD = val.STCD;
          var evt = new Object();
          evt.graphic = new Object();
          evt.graphic.attributes = new Object();
          evt.graphic.attributes.itype = "shuikushuiqing";
          evt.graphic.attributes.rowinfo = JSON.parse(_JSON$stringify(row));
          evt.graphic.attributes.STNM = value;
          evt.graphic.attributes.STCD = STCD;
          evt.graphic.attributes.rowinfo.STNM = value;
          evt.graphic.attributes.rowinfo.STCD = STCD;
          evt.graphic.attributes.rowinfo.tableType = {
            sksq: "hourTable"
          };
          var v = new Object();
          v.itype = "shuikushuiqing";
          console.log("evt", evt);

          _this.$App.showDrawer(evt, v);
        }
      });
    },
    timechange: function timechange() {
      console.log(this.form.date);
      this.Reload();
    },
    XZQHsearch: function XZQHsearch() {
      console.log(this.form.model_adress);
      this.Reload(this.STinfo.STCD);
    },
    QDsearch: function QDsearch() {
      console.log(this.form.model_qudao);
      this.Reload(this.STinfo.STCD);
    },
    STGRsearch: function STGRsearch() {
      console.log(this.form.gsdw);
      this.Reload(this.STinfo.STCD);
    },
    LEVELsearch: function LEVELsearch() {
      console.log(this.form.skdj);
      this.Reload(this.STinfo.STCD);
    },
    Reload: function Reload(agmt) {
      var _this2 = this;

      this.loading = true;

      if (this.form.model_adress.length == 0) {
        this.form.xzqh = '';
      }

      if (this.form.model_adress.length == 1) {
        var str1 = this.form.model_adress[0];
        str1 = str1.substring(0, 6);
        this.form.xzqh = str1;
      }

      if (this.form.model_adress.length == 2) {
        var str2 = this.form.model_adress[1];
        str2 = str2.substring(0, 9);
        this.form.xzqh = str2;
      }

      if (this.form.model_adress.length == 3) {
        var str3 = this.form.model_adress[2];
        str3 = str3.substring(0, 12);
        this.form.xzqh = str3;
      }

      if (this.form.model_qudao.length == 0) {
        this.form.qd = '';
      }

      if (this.form.model_qudao.length == 1) {
        // var str4 = this.form.model_qudao[0];
        // str4 = str4.substring(0, 5);
        this.form.qd = this.form.model_qudao[0];
      }

      if (this.form.model_qudao.length == 2) {
        // var str5 = this.form.model_qudao[1];
        // str5 = str5.substring(0, 9);
        this.form.qd = this.form.model_qudao[1];
      }

      var obj = {
        Time_min: this.form.date[0],
        Time_max: this.form.date[1],
        ADDVCD: this.form.xzqh,
        Canal_Code: this.form.qd,
        STGR: this.form.gsdw,
        LEVEL: this.form.skdj,
        STCD: agmt
      };
      this.axios.get('/guanqu/shuikushuiqing/lishi_xiaoshibiao?' + this.searchs, {
        params: obj
      }).then(function (res) {
        _this2.loading = false;
        console.log(res); // this.data = res.data[0].tongji;

        _this2.data = res.data[0].tongji.map(function (val, index) {
          // debugger;
          for (var key in val) {
            if (/平均水位$/.test(key)) {
              val[key] = _this2.Float_Filter(val[key], 2);
            } else if (/水势$/.test(key)) {
              val[key] = val[key] > 0 ? '↑' : val[key] < 0 ? '↓' : '-'; // val[key].color = val[key]>0?'red':(val[key]<0?'blue':'black')
            } else if (/入库流量$/.test(key) || /出库流量$/.test(key) || /小时蓄水量$/.test(key)) {
              val[key] = _this2.Float_Filter(val[key], 3);
            } else if (val[key] === 0) {
              val[key] = "";
            }
          }

          return val;
        });
        _this2.cols1 = res.data[0].zhandian;
        _this2.list_input.total = res.data[0].tongji.length; //行政区划

        _this2.getTableData_WRP_AD_B(function (data) {
          _this2.form.adressList = data;
        }); // 获取输排水渠道数据,然后设置渠道选择框选项


        _this2.getTableData_WRP_IrrBTCanalSystem(function (data) {
          _this2.form.qudaoList = data;
        });
      });
    },
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = '&STNM=' + this.form.searchmsg;
        this.Reload();
      } else {
        this.searchs = '';
        this.Reload();
      }
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.data[0].ID = index * this.list_input.pagesize;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    },
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    }
  },
  components: {},
  created: function created() {
    console.dir("showDrawer", this.$showDrawer);
  }
};
