      }]]],
      pickerOptions: {
        shortcuts: [{
          text: '前一时段',
          onClick: function onClick(picker) {
            var start = picker.date;
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', start);
          }
        }, {
          text: '后一时段',
          onClick: function onClick(picker) {
            var start = picker.date;
            start.setTime(start.getTime() + 3600 * 1000 * 24 * 7);
            picker.$emit('pick', start);
          }
        }]
      }
    };
  },
  mounted: function mounted() {
    var now = new Date();
    var year = now.getFullYear();
    var month = now.getMonth() + 1;

    if (month < 10) {
      month = '0' + month;
    }

    var day = now.getDate();

    if (day < 10) {
      day = '0' + day;
    }

    this.datecon = year + '-' + month + '-' + day;
    this.getDates(now);
    this.Reload();
  },
  methods: {
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      this.maninfos = [[[{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }]], [[{
        name: '',
        phone: ''
      }], [{
