      }]], [[{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }]], [[{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }]], [[{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }]], [[{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }], [{
        name: '',
        phone: ''
      }]]]; //调用后台函数，传递参数

      this.axios.get('/alarm/getdutyplan', {
        params: {
          begintime: this.datearray[0],
          endtime: this.datearray[6]
        }
      }).then(function (res) {
        _this.loading = false;
        var tabledata = res.data;

        if (tabledata.length > 0) {
          _this.delsign = 1;

          for (var i = 0; i < tabledata.length; i++) {
            var h, s;

            for (var t = 0; t < _this.datearray.length; t++) {
              if (tabledata[i].OLANDATE == _this.datearray[t]) {
                h = t;
                break;
              }
            }

            s = tabledata[i].TYPE - 1;
            var manarray = _this.maninfos[h][s];

            if (manarray[0].name == '') {
              manarray[0].name = tabledata[i].NAME;
              manarray[0].phone = tabledata[i].PHONE;
            } else {
              var obj_m = new Object();
              obj_m.name = tabledata[i].NAME;
              obj_m.phone = tabledata[i].PHONE;
              manarray.push(obj_m);
            }
          }
        } else {
          _this.delsign = 0;
        }
      });
    },
    deleteItem: function deleteItem(i, j, index) {
      this.maninfos[i][j].splice(index, 1);
    },
    addItem: function addItem(i, j) {
      var obj_i = new Object();
      obj_i.name = '';
      obj_i.phone = '';
      this.maninfos[i][j].push(obj_i);
    },
    getDates: function getDates(curdate) {
      var timesStamp = curdate.getTime();
      var currenDay = curdate.getDay();
      this.datearray = [];

      for (var i = 0; i < 7; i++) {
        var temp_date = new Date(timesStamp + 24 * 60 * 60 * 1000 * (i - (currenDay + 6) % 7));
        var year = temp_date.getFullYear();
        var month = temp_date.getMonth() + 1;

        if (month < 10) {
          month = '0' + month;
        }

        var day = temp_date.getDate();

        if (day < 10) {
          day = '0' + day;
        }

        var weekday = year + '-' + month + '-' + day;
        this.datearray.push(weekday);

        if (i == 0) {
          this.FullYear = year.toString();
        }
      }
    },
    timechange: function timechange(date) {
      this.datecon = date;
      var str = date.replace(/-/g, '/');
      var curdate = new Date(str);
      this.getDates(curdate);
      this.Reload();
    },
    onSave: function onSave() {
      var _this2 = this;

      var postData = new Array();

      for (var i = 0; i < 7; i++) {
        for (var j = 0; j < 3; j++) {
          var array = this.maninfos[i][j];

          for (var h = 0; h < array.length; h++) {
            var obj_t = new Object();
            obj_t.name = array[h].name;
            obj_t.phone = array[h].phone;
            var reg = /^(\(\d{3,4}\)|\d{3,4}-|\s)?\d{7,14}$/;

            if (obj_t.name.length > 10) {
              this.$message({
                message: this.datearray[i] + ' ' + this.showdate(i) + this.showtype(j) + '：姓名' + obj_t.name + '异常！',
                type: 'warning'
              });
              return;
            }

            if (obj_t.phone != null && obj_t.phone.trim() != '' && !reg.test(obj_t.phone)) {
              this.$message({
                message: this.datearray[i] + ' ' + this.showdate(i) + this.showtype(j) + '：手机号' + obj_t.phone + '异常！',
                type: 'warning'
              });
              return;
            }

            if ((obj_t.name == null || obj_t.name.trim() == '') && (obj_t.phone == null || obj_t.phone.trim() == '')) {
              continue;
            } else if (obj_t.name != null && obj_t.name.trim() != '' && (obj_t.phone == null || obj_t.phone.trim() == '')) {
              this.$message({
                message: this.datearray[i] + ' ' + this.showdate(i) + this.showtype(j) + '：' + obj_t.name + '对应的手机号码请输入！',
                type: 'warning'
              });
              return;
            } else if ((obj_t.name == null || obj_t.name.trim() == '') && obj_t.phone != null && obj_t.phone.trim() != '') {
              this.$message({
                message: this.datearray[i] + ' ' + this.showdate(i) + this.showtype(j) + '：' + obj_t.phone + '对应的姓名请输入！',
                type: 'warning'
              });
              return;
            } else {
              obj_t.olandate = this.datearray[i];
              obj_t.type = j + 1;
              postData.push(obj_t);
            }
          }
        }
      }

      if (postData.length < 1) {
        this.$message({
          message: '请输入人员值班安排信息！',
          type: 'warning'
        });
        return;
      }

      this.loading = true;
      var postParam = new Object();
      postParam["list"] = postData;
      postParam["delsign"] = this.delsign;
      postParam["begintime"] = this.datearray[0];
      postParam["endtime"] = this.datearray[6];
      this.axios.post('/alarm/adddutyplan', postParam).then(function (res) {
        if (res.data == "ok") {
          _this2.loading = false;

          if (_this2.delsign == 0) {
            _this2.delsign = 1;
          }

          _this2.$message({
            message: '人员值班安排信息保存成功！',
            type: 'success'
          });
        }
      });
    },
    showdate: function showdate(sign) {
      var str = '';

      switch (sign) {
        case 0:
          str = '星期一';
          break;

        case 1:
          str = '星期二';
          break;

        case 2:
          str = '星期三';
          break;

        case 3:
          str = '星期四';
          break;

        case 4:
          str = '星期五';
          break;

        case 5:
          str = '星期六';
          break;

        case 6:
          str = '星期日';
          break;
      }

      return str;
    },
    showtype: function showtype(sign) {
      var str = '';

      switch (sign) {
        case 0:
          str = '主管领导';
          break;

        case 1:
          str = '值班领导';
          break;

        case 2:
          str = '值班人员';
          break;
      }

      return str;
    },
    showManinfo: function showManinfo(i, j) {
      var obj_t = new Object();
      obj_t.i = i;
      obj_t.j = j;
      obj_t.itype = 'zhibanrenyuan';
      obj_t.list = [];
      this.$emit('showdailog', obj_t);
    },
    showphone: function showphone(dataobj) {
      if (dataobj.list == null || dataobj.list.length == 0) {
        return;
      }

      var curarray = this.maninfos[dataobj.i][dataobj.j];
      var manlist = dataobj.list;

      for (var h = 0; h < manlist.length; h++) {
        if (curarray.length == 1 && (curarray[0].name == null || curarray[0].name.trim() == '')) {
          curarray[0].name = manlist[h].NM;
          curarray[0].phone = manlist[h].MOBILE;
        } else {
          var obj_t = new Object();
          obj_t.name = manlist[h].NM;
          obj_t.phone = manlist[h].MOBILE;
          var write = 0;

          for (var s = 0; s < curarray.length; s++) {
            if (curarray[s].name == obj_t.name) {
              if (curarray[s].phone == null || curarray[s].phone == '') {
                curarray[s].phone = obj_t.phone;
              } else {
                write = 1;
                break;
              }
            }
          }

          if (write == 0) {
            curarray.push(obj_t);
          }
        }
      }
    }
  }
};
