import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
export default {
  data: function data() {
    return {
      loading: false,
      date: '',
      data: [],
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        zdlx: '',
        model_qudao: '',
        date: '',
        searchmsg: '',
        xzqh: '',
        qd: ''
      },
      gclx: {
        SourceE_Type: [],
        Field: ''
      },
      sylx: {
        Source_Type: [],
        Field: ''
      },
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      searchs: ''
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    // 工程类型选择器的方法
    sourceEUpdate: function sourceEUpdate() {},
    // 水源类型选择器的方法
    sourceUpdate: function sourceUpdate() {},
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    },
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = '&SourceE_Code=like,' + this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.pagesize * (this.list_input.current - 1);
    },
    timechange: function timechange(date) {
      console.log(date);
      this.date = date;
    },
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      var obj = {
        Source_Type: this.sylx.Source_Type,
        _orderby: '',
        SourceE_Type: '',
        RSNM: ''
      };
      this.axios.get('/guanqu/jichushuiyuanxinxi/shuiyuan?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: obj
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.data = res.data.list;
        _this.list_input.total = res.data.total;

        for (var i = 0; i < _this.data.length; i++) {}
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=SourceE_Type').then(function (res) {
        _this.gclx.SourceE_Type = res.data.list;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Source_Type').then(function (res) {
        _this.sylx.Source_Type = res.data.list;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  },
  created: function created() {
    var _this2 = this;

    // 获取输排水渠道数据,然后设置渠道选择框选项
    this.getTableData_WRP_IrrBTCanalSystem(function (data) {
      _this2.form.qudaoList = data;
    });
  }
};
