import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
import { setInterval, setTimeout } from "timers";
import { truncateSync } from "fs";
export default {
  data: function data() {
    return {
      imglist: [],
      theight: window.screen.height,
      subheight1: window.screen.height * 0.25,
      lwidth: window.screen.width * 0.28125,
      cwidth: window.screen.width * 0.4375,
      imgwidth: window.screen.height * 2055 / 1703 * 1.19,
      imgheight: window.screen.height * 1.19,
      allwidth: window.screen.width,
      leftwidth: window.screen.width * 0.28125 * 0.7,
      showleft: true,
      showright: true,
      videosign: false,
      videotop: (window.screen.height - 680) / 2,
      videoleft: (window.screen.width - 900) / 2,
      videosrc: '',
      form: {},
      canallist: [],
      gatelist: []
    };
  },
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    var _this = this;

    this.loadRealData();
    this.loadline();
    this.loadImgdata();
    this.loadPiechart();
    setInterval(function () {
      _this.reloadData();
    }, 1000 * 60 * 5);
  },
  methods: {
    toggleFullScreen: function toggleFullScreen() {
      var el = document.documentElement;

      if (el.requestFullscreen) {
        el.requestFullscreen();
      } else if (el.mozRequestFullScreen) {
        el.mozRequestFullScreen();
      } else if (el.webkitRequestFullscreen) {
        el.webkitRequestFullscreen();
      } else if (el.msRequestFullscreen) {
        el.msRequestFullscreen();
      }
    },
    //重新加载数据
    reloadData: function reloadData() {
      this.loadRealData();
      this.loadline();
      this.loadImgdata();
      this.loadPiechart();
    },
    //绘制线性图
    loadline: function loadline() {
      var _this2 = this;

      var body = {
        STCD: 'SMB0000002',
        _page_size: 999999
      }; // 时间参数

      var DTT = this.$FilterData.elDatePicker_Filter(this.$App.zeroPointToNowOfTheDay()).split(",");
      body.Time_min = DTT[1];
      body.Time_max = DTT[2];
      this.$GetData.Survey_History_QDSQ("historyTable", body, {
        default: true
      }, function (data) {
        _this2.createChart('firstchart', data.data, '凉水干渠渠首');
      });
      var body2 = {
        STCD: 'SMB0000003',
        _page_size: 999999
      };
      body2.Time_min = DTT[1];
      body2.Time_max = DTT[2];
      this.$GetData.Survey_History_QDSQ("historyTable", body2, {
        default: true
      }, function (data) {
        _this2.createChart('secondchart', data.data, '团岭干渠渠首');
      });
      var body3 = {
        STCD: 'SMB0000019',
        _page_size: 999999
      };
      body3.Time_min = DTT[1];
      body3.Time_max = DTT[2];
      this.$GetData.Survey_History_QDSQ("historyTable", body3, {
        default: true
      }, function (data) {
        _this2.createChart('threechart', data.data, '孙家冲平衡站');
      });
      var body4 = {
        STCD: 'SMB0000016',
        _page_size: 999999
      };
      body4.Time_min = DTT[1];
      body4.Time_max = DTT[2];
      this.$GetData.Survey_History_QDSQ("historyTable", body4, {
        default: true
      }, function (data) {
        _this2.createChart('fourchart', data.data, '石门桥干渠渠首');
      });
    },
    //加载渠道和闸阀水情实时数据
    loadRealData: function loadRealData() {
      var _this3 = this;

      this.axios.get('/fieldinfo/getrinfo').then(function (res) {
        var obj = res.data;
        _this3.canallist = obj.typecanal;

        for (var i = 0; i < _this3.canallist.length; i++) {
          var canalobj = _this3.canallist[i];

          if (canalobj.Z > 0) {
            document.getElementById(canalobj.STCD).className = "waterqddiv";
          }

          if (canalobj.ALARM == 1) {
            document.getElementById(canalobj.STCD).className = "warmqddiv";
          }
        }

        _this3.gatelist = obj.typegate;
      });
    },
    //加载图像站数据
    loadImgdata: function loadImgdata() {
      var _this4 = this;

      this.imglist = [];
      this.axios.get('/fieldinfo/videodata').then(function (res) {
        var list = res.data;

        for (var i = 0; i < list.length; i++) {
          var img_obj = new Object();
          img_obj.stnm = list[i].STNM;
          img_obj.stcd = list[i].STCD;
          img_obj.puid = list[i].PUID;

          _this4.imglist.push(img_obj);
        }
      });
    },
    //加载饼状图
    loadPiechart: function loadPiechart() {
      var _this5 = this;

      this.axios.get('/fieldinfo/piechart').then(function (res) {
        var list = res.data;
        var echartData = {
          y: {
            list: []
          }
        };

        if (list.length == 1) {
          if (list[0].CS == 0) {
            var p1 = new Object();
            p1.value = list[0].TOTAL;
            p1.name = list[0].TITLE;
            echartData.y.list.push(p1);
            var p2 = new Object();
            p2.value = 0;
            p2.name = "正常";
            echartData.y.list.push(p2);
          } else {
            var p2 = new Object();
            p2.value = 0;
            p2.name = "故障";
            echartData.y.list.push(p2);
            var p1 = new Object();
            p1.value = list[0].TOTAL;
            p1.name = list[0].TITLE;
            echartData.y.list.push(p1);
          }
        } else if (list.length > 1) {
          for (var h = 0; h < list.length; h++) {
            var p_obj = new Object();
            p_obj.value = list[h].TOTAL;
            p_obj.name = list[h].TITLE;
            echartData.y.list.push(p_obj);
          }
        }

        _this5.createPiechart(echartData);
      });
    },
    // 水位流量线性图
    createChart: function createChart(id, data, stnm) {
      if (data.length > 0) {
        var echartData = this.$App.transform_QDSQ_data_into_ehart_data(data, "historyTable"); // 渠道水情历史统计表数据 转 ehart图形用数据 返回一个对象, 对象里分别装 Y1轴对象 Y2轴对象 X轴对象

        var x_List = echartData.x.list.map(function (val, index, array) {
          // 时间过滤
          var time = val.split(" ");
          return time[1];
        });
        myChart = this.$echarts.init(document.getElementById(id));
        myChart.setOption({
          title: {
            text: stnm + "今日水情图",
            textStyle: {
              fontSize: 14,
              fontWeight: 'bolder',
              color: '#fff'
            }
          },
          backgroundColor: '',
          tooltip: {
            trigger: "axis"
          },
          color: ['#f6ff00', '#00ffa2'],
          legend: {
            icon: 'roundRect',
            //icon为圆角矩形          
            data: [echartData.y1.name, echartData.y2.name],
            x: "right",
            textStyle: {
              color: '#fff'
            }
          },
          grid: {
            left: '3%',
            //图表距边框的距离
            right: '4%',
            bottom: '1%',
            containLabel: true,
            backgroundColor: 'rgba(280,280,280,0.4)',
            show: true
          },
          animation: false,
          xAxis: [{
            type: "category",
            boundaryGap: false,
            data: x_List,
            splitLine: {
              show: false
            },
            //去除网格线
            axisLine: {
              lineStyle: {
                type: 'solid',
                color: '#ebebe3',
                width: '2'
              }
            },
            axisTick: {
              interval: function interval(index, val) {
                var listLength = echartData.x.list.length;

                if (listLength >= 4) {
                  var tick = Math.round(listLength / 4);

                  if ((index + 1) % tick === 0) {
                    return true;
                  } else {
                    return false;
                  }
                } else {
                  return true;
                }
              },
              length: 10
            },
            axisLabel: {
              interval: function interval(index, val) {
                var listLength = echartData.x.list.length;

                if (listLength >= 4) {
                  var tick = Math.round(listLength / 4);
                  var a = (index + 1) % tick;

                  if ((index + 1) % tick === 0) {
                    return true;
                  } else {
                    return false;
                  }
                } else {
                  return true;
                }
              },
              rotate: 0,
              margin: 16,
              textStyle: {
                color: '#fff'
              }
            }
          }],
          yAxis: [{
            name: "".concat(echartData.y1.name, " m\xB3/s"),
            type: "value",
            splitLine: {
              show: false
            },
            //去除网格线
            axisLine: {
              lineStyle: {
                type: 'solid',
                color: '#ebebe3',
                width: '2'
              }
            },
            axisLabel: {
              formatter: "{value} ",
              textStyle: {
                color: '#fff'
              }
            },
            scale: true
          }, {
            name: "".concat(echartData.y2.name, "m"),
            type: "value",
            splitLine: {
              show: false
            },
            //去除网格线
            axisLine: {
              lineStyle: {
                type: 'solid',
                color: '#ebebe3',
                width: '2'
              }
            },
            axisLabel: {
              formatter: "{value} ",
              textStyle: {
                color: '#fff'
              }
            },
            scale: true
          }],
          series: [{
            name: echartData.y1.name,
            type: "line",
            data: echartData.y1.list,
            symbol: 'none',
            smooth: true,
            itemStyle: {
              normal: {
                lineStyle: {
                  color: '#f6ff00',
                  width: 2,
                  opacity: 1
                }
              }
            }
          }, {
            name: echartData.y2.name,
            type: "line",
            data: echartData.y2.list,
            yAxisIndex: 1,
            symbol: 'none',
            smooth: true,
            itemStyle: {
              normal: {
                lineStyle: {
                  color: '#00ffa2',
                  width: 2,
                  opacity: 1
                }
              }
            }
          }]
        });
      } else {
        var myChart = document.getElementById(id);
        myChart.style.display = "flex";
        myChart.style.alignItems = "center";
        myChart.style.justifyContent = "center";
        myChart.innerHTML = "<font style='color:#fff;font-weight:bold;'>" + stnm + "暂无今天数据</font>";
      }
    },
    //饼状图
    createPiechart: function createPiechart(edata) {
      var myChart = this.$echarts.init(this.$refs.piechart); // 指定图表的配置项和数据

      var option = {
        // 标题
        title: {
          text: '站点运行工况故障分布图',
          x: 'center',
          top: 10,
          textStyle: {
            fontSize: 14,
            fontWeight: 'bold',
            color: '#fff'
          }
        },
        //自定义的颜色
        color: ['#ef2424', '#12d0ff'],
        // 图例
        legend: {
          data: ['正常', '故障'],
          orient: 'vertical',
          x: 'right',
          y: 'center',
          textStyle: {
            color: '#fff'
          }
        },
        grid: {
          bottom: '2%'
        },
        // 数据
        series: [{
          name: '分布情况',
          type: 'pie',
          radius: '65%',
          center: ['50%', '55%'],
          data: edata.y.list,
          label: {
            normal: {
              textStyle: {
                color: 'rgba(255, 255, 255, 1)'
              }
            }
          },
          labelLine: {
            normal: {
              lineStyle: {
                color: 'rgba(255, 255, 255, 1)'
              },
              smooth: 0.2,
              length: 10,
              length2: 20
            }
          },
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(255, 255, 255, 0.9)'
            },
            normal: {
              label: {
                show: true,
                formatter: '{b} : {c} ({d}%)'
              },
              labelLine: {
                show: true
              },
              // 设置扇形的阴影
              shadowBlur: 30,
              shadowColor: 'rgba(6,121,159,0.8)',
              shadowOffsetX: -10,
              shadowOffsetY: 10
            }
          }
        }]
      }; // 使用刚指定的配置项和数据显示图表。

      myChart.setOption(option);
    },
    showvideo: function showvideo(puid) {
      if (!!window.ActiveXObject || "ActiveXObject" in window) {
        try {
          var obj = new ActiveXObject("DPSDK_OCX.DPSDK_OCXCtrl.1");
        } catch (e) {
          this.$message({
            message: '控件未注册，请先注册控件！',
            type: 'warning'
          });
          var url = '/file/downloadocx';
          window.location.href = url;
          return;
        }
      } else {
        this.$message({
          message: '请确保在IE浏览器下使用视频监控功能！',
          type: 'warning'
        });
        return;
      }

      this.videosign = true;
      this.videosrc = "webdemo/index.html?" + puid;
    },
    hidediv: function hidediv() {
      this.videosign = false;
    },
    //显示渠道信息
    showcanalinfo: function showcanalinfo(stcd) {
      this.form = new Object();

      for (var i = 0; i < this.canallist.length; i++) {
        var canalobj = this.canallist[i];

        if (canalobj.STCD == stcd) {
          this.form = canalobj;
          break;
        }
      }
    },
    //显示闸阀信息
    showgateinfo: function showgateinfo(stcd) {
      this.form = new Object();

      for (var i = 0; i < this.gatelist.length; i++) {
        var gateobj = this.gatelist[i];

        if (gateobj.STCD == stcd) {
          this.form = gateobj;
          break;
        }
      }
    },
    switchleft: function switchleft() {
      this.showleft = !this.showleft;

      if (this.showleft) {
        this.$refs.switchleft.style.left = this.lwidth + 'px';
      } else {
        this.$refs.switchleft.style.left = '0px';
      }
    },
    switchright: function switchright() {
      this.showright = !this.showright;

      if (this.showright) {
        this.$refs.switchright.style.right = this.lwidth + 'px';
      } else {
        this.$refs.switchright.style.right = '0px';
      }
    }
  }
};
