export default {
  data: function data() {
    return {
      loading: false,
      date: '',
      data: [],
      form: {
        searchmsg: ''
      },
      gw: {
        Post_Code: [],
        Field: ''
      },
      xb: {
        Sex: [],
        Field: ''
      },
      zc: {
        Positional_Title: [],
        Field: ''
      },
      xl: {
        Educastional_Level: [],
        Field: ''
      },
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      searchs: ''
    };
  },
  mounted: function mounted() {
    this.GetFilterMethods();
    this.Reload();
  },
  methods: {
    GWsearch: function GWsearch() {
      this.Reload();
    },
    XBsearch: function XBsearch() {
      this.Reload();
    },
    ZCsearch: function ZCsearch() {
      this.Reload();
    },
    XLsearch: function XLsearch() {
      this.Reload();
    },
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    },
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = '&Personal_Name=like,' + this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.pagesize * (this.list_input.current - 1);
    },
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      this.axios.get('/guanqu/admin/WRP_IrrBSPerson?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: {
          Post_Code: this.gw.Field,
          Sex: this.xb.Field,
          Positional_Title: this.zc.Field,
          Educastional_Level: this.xl.Field
        }
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.data = res.data.list;
        _this.list_input.total = res.data.total;

        for (var i = 0; i < _this.data.length; i++) {}
      });
    },
    //获取筛选数据
    GetFilterMethods: function GetFilterMethods() {
      var _this2 = this;

      //获取岗位筛选
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Post_Code').then(function (res) {
        _this2.gw.Post_Code = res.data.list;
      }); //获取性别筛选

      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Sex').then(function (res) {
        _this2.xb.Sex = res.data.list;
      }); //获取职称筛选

      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Positional_Title').then(function (res) {
        _this2.zc.Positional_Title = res.data.list;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Educastional_Level').then(function (res) {
        _this2.xl.Educastional_Level = res.data.list;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  }
};
