export default {
  data: function data() {
    return {
      loading: false,
      date: '',
      data: [],
      sygclb: {
        IARHWPRTP: [],
        Field: ''
      },
      gqgm: {
        IRASC: [],
        Field: ''
      },
      form: {
        searchmsg: ''
      },
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      },
      searchs: ''
    };
  },
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    //灌区规模筛选
    GQGMsearch: function GQGMsearch() {
      this.Reload();
    },
    //水源工程筛选
    SYGCsearch: function SYGCsearch() {
      this.Reload();
    },
    //未开发功能提示
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    },
    //搜索框方法
    search: function search() {
      if (this.form.searchmsg !== '') {
        this.searchs = '&IRANM=' + this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    //自定义列表首列id
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.pagesize * (this.list_input.current - 1);
    },
    //时间选择器
    timechange: function timechange(date) {
      console.log(date);
      this.date = date;
    },
    //获取列表数据
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      this.axios.get('/guanqu/jichuxinxi/guanqu_jiben?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: {
          IARHWPRTP: this.sygclb.Field,
          IRASC: this.gqgm.Field
        }
      }).then(function (res) {
        // IRANM:this.form.searchmsg
        console.log(res);
        _this.loading = false;
        _this.data = res.data.list;
        _this.list_input.total = res.data.total;

        for (var i = 0; i < _this.data.length; i++) {}
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=IARHWPRTP').then(function (res) {
        _this.sygclb.IARHWPRTP = res.data.list;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=IRASC').then(function (res) {
        _this.gqgm.IRASC = res.data.list;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  }
};
