export default {
  data: function data() {
    return {
      theight: window.innerHeight - 290,
      searchmsg: '',
      marginleft: window.innerWidth * 19 / 24 * 0.5 - 150,
      Treedata: [],
      defaultProps: {
        children: 'children',
        label: 'STNM'
      },
