  mounted: function mounted() {
    var _this = this;

    var dateformat = this.getNowDate();
    this.formb.tm = dateformat;
    this.formx.tm = dateformat;
    this.forms.tm = dateformat;
    this.axios.get('/ldresult/typesite').then(function (res) {
      var root = new Object();
      root["STNM"] = "类型";
      root["ZD_SIGN"] = 0;
      root["STCD"] = "0";
      var typelist = new Array();
      var type1 = new Object();
      type1["STNM"] = "标准断面";
      type1["ME_TYPE"] = 1;
      type1["ZD_SIGN"] = 0;
      type1["STCD"] = "1";
      typelist.push(type1);
      var type2 = new Object();
      type2["STNM"] = "巴歇尔槽";
      type2["ME_TYPE"] = 2;
      type2["ZD_SIGN"] = 0;
      type2["STCD"] = "2";
      typelist.push(type2);
