      type3["STNM"] = "水闸";
      type3["ME_TYPE"] = 3;
      type3["ZD_SIGN"] = 0;
      type3["STCD"] = "3";
      typelist.push(type3); //var type4=new Object();
      //type4["STNM"]="管道";type4["ME_TYPE"]=4;type4["ZD_SIGN"]=0;type4["STCD"]="4";typelist.push(type4);

      root.children = typelist;
      var keyarry = ["0", "1"];
      var datalist = res.data;
      typelist.map(function (val) {
        val.children = getChildrenList(val.ME_TYPE, datalist);
        return val;
      });

      function getChildrenList(me_type, data) {
        var newList;
        newList = data.filter(function (val, index) {
          if (val.ME_TYPE == me_type) {
            return true;
          }
        });
        return newList;
      }

      keyarry.push(typelist[0].children[0].STCD);

      _this.Treedata.push(root);

      _this.keys = keyarry;
      _this.STinfo = typelist[0].children[0];
      var infoobj = _this.STinfo;

      _this.axios.get('/ldresult/maxinfo', {
        params: {
          metype: _this.STinfo.ME_TYPE,
          stcd: _this.STinfo.STCD,
          year: _this.formb.tm
        }
      }).then(function (res1) {
        var maxobj = res1.data;

        _this.loadTData(maxobj);
      });
    });
  },
  methods: {
    //加载表格数据
    loadTData: function loadTData(maxobj) {
      this.Titileinfo = maxobj;

      if (maxobj.TYPES) {
        if (maxobj.TYPES == 1) {
          this.Titileinfo.TYPENAME = '矩形';
        } else if (maxobj.TYPES == 2) {
          this.Titileinfo.TYPENAME = '梯形';
        } else if (maxobj.TYPES == 3) {
          this.Titileinfo.TYPENAME = 'U形';
        }
      }
    },
    //站点名称查询
    search: function search() {
      var _this2 = this;

      this.Treedata = new Array();
      var tt = this.STinfo;
      this.axios.get('/ldresult/typesite', {
        params: {
          stnm: this.searchmsg
        }
      }).then(function (res) {
        var root = new Object();
        root["STNM"] = "类型";
        root["ZD_SIGN"] = 0;
        root["STCD"] = "0";
        var typelist = new Array();
        var type1 = new Object();
        type1["STNM"] = "标准断面";
        type1["ME_TYPE"] = 1;
        type1["ZD_SIGN"] = 0;
        type1["STCD"] = "1";
        typelist.push(type1);
        var type2 = new Object();
        type2["STNM"] = "巴歇尔槽";
        type2["ME_TYPE"] = 2;
        type2["ZD_SIGN"] = 0;
        type2["STCD"] = "2";
        typelist.push(type2);
        var type3 = new Object();
        type3["STNM"] = "水闸";
        type3["ME_TYPE"] = 3;
        type3["ZD_SIGN"] = 0;
        type3["STCD"] = "3";
        typelist.push(type3); //var type4=new Object();
        //type4["STNM"]="管道";type4["ME_TYPE"]=4;type4["ZD_SIGN"]=0;type4["STCD"]="4";typelist.push(type4);

        var keyarry = ["0"];
        var datalist = res.data;
        typelist.map(function (val) {
          val.children = getChildrenList(val.ME_TYPE, datalist);
          return val;
        });

        function getChildrenList(me_type, data) {
          var newList;
          newList = data.filter(function (val, index) {
            if (val.ME_TYPE == me_type) {
              return true;
            }
          });
          return newList;
        }

        for (var i = typelist.length - 1; i >= 0; i--) {
          var typeobj = typelist[i];

          if (typeobj.children == null || typeobj.children.length == 0) {
            typelist.splice(i, 1);
          }
        }

        keyarry.push(typelist[0].ME_TYPE);
        keyarry.push(typelist[0].children[0].STCD);
        root.children = typelist;

        _this2.Treedata.push(root);

        _this2.keys = keyarry;
        _this2.STinfo = typelist[0].children[0];

        _this2.axios.get('/ldresult/maxinfo', {
          params: {
            metype: _this2.STinfo.ME_TYPE,
            stcd: _this2.STinfo.STCD,
            year: _this2.formb.tm
          }
        }).then(function (res) {
          var maxobj = res.data;

          _this2.loadTData(maxobj);

          if (_this2.STinfo.ME_TYPE == 1) {
            _this2.$refs['div1'].style.display = "";
            _this2.$refs['div2'].style.display = "none";
            _this2.$refs['div3'].style.display = "none";
          } else if (_this2.STinfo.ME_TYPE == 2) {
            _this2.$refs['div1'].style.display = "none";
            _this2.$refs['div2'].style.display = "";
            _this2.$refs['div3'].style.display = "none";
          } else if (_this2.STinfo.ME_TYPE == 3) {
            _this2.$refs['div1'].style.display = "none";
            _this2.$refs['div2'].style.display = "none";
            _this2.$refs['div3'].style.display = "";
          }

          _this2.InitData();
        });
      });
    },
    //点击站点叶节点事件
    type_tree: function type_tree(data) {
      var _this3 = this;

      if (data.ZD_SIGN == 1) {
        this.STinfo = data;

        if (data.ME_TYPE == 1 || data.ME_TYPE == 2 || data.ME_TYPE == 3) {
          this.axios.get('/ldresult/maxinfo', {
            params: {
              metype: data.ME_TYPE,
              stcd: data.STCD,
              year: this.formb.tm
            }
          }).then(function (res) {
            var maxobj = res.data;

            _this3.loadTData(maxobj);

            if (_this3.STinfo.ME_TYPE == 1) {
              _this3.$refs['div1'].style.display = "";
              _this3.$refs['div2'].style.display = "none";
              _this3.$refs['div3'].style.display = "none";
            } else if (_this3.STinfo.ME_TYPE == 2) {
              _this3.$refs['div1'].style.display = "none";
              _this3.$refs['div2'].style.display = "";
              _this3.$refs['div3'].style.display = "none";
            } else if (_this3.STinfo.ME_TYPE == 3) {
              _this3.$refs['div1'].style.display = "none";
              _this3.$refs['div2'].style.display = "none";
              _this3.$refs['div3'].style.display = "";
            }

            _this3.InitData();
          });
        }
      }
    },
    //重置表单
    InitData: function InitData() {
      var dateformat = this.getNowDate();
      this.formb.tm = dateformat;
      this.formx.tm = dateformat;
      this.forms.tm = dateformat;
      this.formb.z = '';
      this.formx.z = '';
      this.forms.fz = '';
      this.forms.bz = '';
      this.forms.hi = '';
      this.formb.q = '';
      this.formx.q = '';
      this.forms.q = '';
    },
    //计算
    onCaculate: function onCaculate(meType) {
      var _this4 = this;

      var regPos = /^\d+(\.\d+)?$/;
      var p_obj = new Object();
      p_obj.metype = this.STinfo.ME_TYPE;

      if (meType == 1) {
        if (this.formb.z == '') {
          this.$message({
            message: '请输入水深！',
            type: 'warning'
          });
          return;
        }

        if (!regPos.test(this.formb.z)) {
          this.$message({
            message: '水深为数字！',
            type: 'warning'
          });
          return;
        }

        p_obj.b = this.Titileinfo.B;
        p_obj.m = this.Titileinfo.M;
        p_obj.n = this.Titileinfo.N;
        p_obj.i = this.Titileinfo.I;
        p_obj.z = this.formb.z;
        p_obj.types = this.Titileinfo.TYPES;

        if (this.Titileinfo == "") {
          this.$message({
            message: this.STinfo.STNM + this.formb.tm + '年标准断面参数不存在！',
            type: 'warning'
          });
          return;
        }
      } else if (meType == 2) {
        if (this.formx.z == '') {
          this.$message({
            message: '请输入水深！',
            type: 'warning'
          });
          return;
        }

        if (!regPos.test(this.formx.z)) {
          this.$message({
            message: '水深为数字！',
            type: 'warning'
          });
          return;
        }

        p_obj.model = this.Titileinfo.MODEL;
        p_obj.z = this.formx.z;
      } else {
        if (this.forms.fz == '') {
          this.$message({
            message: '请输入闸前水深！',
            type: 'warning'
          });
          return;
        }

        if (!regPos.test(this.forms.fz)) {
          this.$message({
            message: '闸前水深为数字！',
            type: 'warning'
          });
          return;
        }

        if (this.forms.bz == '') {
          this.$message({
            message: '请输入闸后水深！',
            type: 'warning'
          });
          return;
        }

        if (!regPos.test(this.forms.bz)) {
          this.$message({
            message: '闸后水深为数字！',
            type: 'warning'
          });
          return;
        }

        if (this.forms.hi == '') {
          this.$message({
            message: '请输入闸门开度！',
            type: 'warning'
          });
          return;
        }

        if (!regPos.test(this.forms.hi)) {
          this.$message({
            message: '闸门开度为数字！',
            type: 'warning'
          });
          return;
        }

        p_obj.gthlntwd = this.Titileinfo.GTHLNTWD;
        p_obj.hi = this.forms.hi;
        p_obj.fz = this.forms.fz;
        p_obj.bz = this.forms.bz;
      }

      this.axios.post('/ldresult/getq', p_obj).then(function (res) {
        var q = res.data;

        if (q != -1 && q != "NaN") {
          if (meType == 1) {
            _this4.formb.q = _parseFloat(q).toFixed(3);
          } else if (meType == 2) {
            _this4.formx.q = _parseFloat(q).toFixed(3);
          } else {
            _this4.forms.q = _parseFloat(q).toFixed(3);
          }
        } else {
          _this4.$message({
            message: '数据输入有误，请核对相关数据!',
            type: 'warning'
          });
        }
      });
    },
    //获取当前日期
    getNowDate: function getNowDate() {
      var date = new Date();
      return date.getFullYear().toString();
