        params: u_obj
      }).then(function (res) {
        if (res.data == "ok") {
          _this6.$message({
            message: '糙率修改成功！',
            type: 'success'
          });
        } else {
          _this6.$message({
            message: _this6.formb.tm + '年无糙率数据！',
            type: 'warm'
          });
        }
      });
    },
    //保存到率定结果表
    onSave: function onSave(meType) {
      var _this7 = this;

      var s_obj = new Object();
      s_obj.stcd = this.STinfo.STCD;
      s_obj.stnm = this.STinfo.STNM;

      if (meType == 1) {
        if (this.formb.q == '') {
          this.$message({
            message: '请先计算流量!',
            type: 'warning'
          });
          return;
        }

        s_obj.year = this.formb.tm;
        s_obj.z = this.formb.z;
        s_obj.q = this.formb.q;
      } else if (meType == 2) {
        if (this.formx.q == '') {
          this.$message({
            message: '请先计算流量!',
            type: 'warning'
          });
          return;
        }

        s_obj.year = this.formx.tm;
        s_obj.z = this.formx.z;
        s_obj.q = this.formx.q;
      } else {
        if (this.forms.q == '') {
          this.$message({
            message: '请先计算流量!',
            type: 'warning'
          });
          return;
        }

        s_obj.year = this.forms.tm;
        s_obj.z = this.forms.fz;
        s_obj.q = this.forms.q;
      }

      this.axios.get('/ldresult/existresult', {
        params: s_obj
      }).then(function (res) {
        if (res.data == 0) {
          _this7.axios.post('/ldresult/addeqrl', s_obj).then(function (res) {
            if (res.data == "ok") {
