import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
export default {
  data: function data() {
    return {
      loading: false,
      date: '',
      data: [],
      form: {
        adressList: [],
        qudaoList: [],
        model_adress: '',
        zdlx: '',
        model_qudao: '',
        date: '',
        searchmsg: '',
        xzqh: '',
        qd: ''
      },
      wjlx: {
        MATType: [],
        Field: ''
      },
      list_input: {
        total: 100,
        pagesize: 50,
        pagesizeopts: [10, 20, 50, 75, 100, 200],
        current: 1
      }
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    err: function err() {
      // console.log("showDrawer",this.$showDrawer);
      this.$Message.warning('该功能还在开发中...');
    },
    search: function search() {
      if (this.form.searchmsg != '') {
        this.searchs = '&MAT=like,' + this.form.searchmsg;
      } else {
        this.searchs = '';
      }

      this.Reload();
    },
    indexMethod: function indexMethod(index) {
      return index + 1 + this.list_input.pagesize * (this.list_input.current - 1);
    },
    timechange: function timechange(date) {
      console.log(date);
      this.date = date;
    },
    Reload: function Reload() {
      var _this = this;

      this.loading = true;
      this.axios.get('/guanqu/admin/WRP_MediaAndText?_page_size=' + this.list_input.pagesize + '&_page=' + this.list_input.current + this.searchs, {
        params: {
          MATType: this.wjlx.MATType
        }
      }).then(function (res) {
        console.log(res);
        _this.loading = false;
        _this.data = res.data.rows;
        _this.list_input.total = res.data.total;

        for (var i = 0; i < _this.data.length; i++) {}
      }); //行政区划

      this.getTableData_WRP_AD_B(function (data) {
        _this.form.adressList = data;
      });
      this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=MATType').then(function (res) {
        _this.wjlx.MATType = res.data.rows;
      });
    },
    // 处理页码切换
    CurrentChange: function CurrentChange(index) {
      // console.log(index);
      this.list_input.current = index;
      this.Reload();
    },
    // 处理每页显示条数切换
    PagesizeChange: function PagesizeChange(pagesize) {
      // console.log(pagesize)
      this.list_input.pagesize = pagesize;
      this.Reload();
    }
  }
};
