export default {
  props: {
    id: ""
  },
  data: function data() {
    return {
      form: {
        id: '',
        login: "",
        name: "",
        passwd: "",
        confirmPasswd: "",
        role: "",
        status: "",
        enterprise: "",
        department: "",
        position: "",
        radio: "",
        birthday: "",
        phone: "",
        remark: ""
      },
      roles: [],
      rules: {
        login: [{
          required: true,
          message: "用户名不能为空"
        }],
        name: [{
          required: true,
          message: "姓名不能为空"
        }],
        passwd: [{
          required: true,
          message: "登录密码不能为空"
        }],
        confirmPasswd: [{
          required: true,
          message: "确认密码不能为空"
        }],
        role: [{
          required: true,
          message: "所属角色不能为空"
        }],
        status: [{
          required: true,
          message: "状态不能为空"
        }]
      }
    };
  },
  mounted: function mounted() {
    this.Reload();
  },
  methods: {
    Reload: function Reload() {
      var _this = this;

      //获取数据
      this.axios.get('/guanqu/web/users/' + this.id).then(function (res) {
        _this.form = res.data;
        _this.form.confirmPasswd = _this.form.passwd;
      });
      this.axios.get('/guanqu/web/sys-roles/page?size=99999').then(function (res) {
        _this.roles = res.data.list;
      });
    },
    del: function del() {
      this.$emit("closewindows");
    },
    Edit: function Edit() {
      var _this2 = this;

      this.$confirm("是否确认修改内容", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        if (!_this2.form.login || !_this2.form.name || !_this2.form.role || !_this2.form.status) {
          _this2.$message({
            type: "error",
            message: "必填项未输入，修改用户失败"
          });
        } else {
          _this2.axios.put('/guanqu/web/users/', {
            birthday: _this2.form.birthday,
            department: _this2.form.department,
            enterprise: _this2.form.enterprise,
            id: _this2.form.id,
            login: _this2.form.login,
            name: _this2.form.name,
            phone: _this2.form.phone,
            position: _this2.form.position,
            remark: _this2.form.remark,
            roleId: _this2.form.role.id,
            sex: _this2.form.sex,
            status: _this2.form.status
          }).then(function (res) {
            _this2.$message({
              type: "success",
              message: "修改用户成功!"
            });

            _this2.Reload();

            _this2.$emit("closewindows");
          });
        }
      }).catch(function () {// this.$message({
        //   type: "error",
        //   message: "网络错误！修改用户失败！"
        // });
      });
    }
  },
  computed: {},
  watch: {}
};
