.zZindex {
  z-index: 3000 !important;
}

 .el-menu-item.is-active { background-color: #fff !important; color:#409EFF !important;border-bottom: 2px solid #fff;  }
 .el-menu-item>img{
  margin-right: 5px;
 }
 .el-menu>.el-menu-item.is-active{
       border-bottom: 2px solid #fff;

 }

#app {
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  overflow: hidden;
}

#listWindow {
  position: absolute;
  top: 100px;
  right: 20px;
  width: 480px;
  min-height: 200px;
  background-color: #fff;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
  padding: 15px;
  box-sizing: border-box;
  z-index: 50;
  // overflow: auto;
  transition: 0.5s;
  // 开关按钮
  .switch {
    position: absolute;
    left: -30px;
    width: 30px;
    height: 80px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    // background: rgb(45, 183, 245);
    // background: #F56C6C;
    background: white;
    // background: white;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    border: rgb(126, 183, 240) 2px solid;
    border-right: 0;

    &:hover {
      filter: brightness(1.1);
    }

    i {
      font-size: 25px;
    }
  }

  // 模式切换按钮
  .mode {
    position: absolute;
    left: -12px;
    width: 28px;
    height: 28px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    // background: rgb(45, 183, 245);
    // background: #F56C6C;
    background: white;
    // background: white;
    border-radius: 50%;
    border: rgb(126, 183, 240) 2px solid;

    &:hover {
      filter: brightness(1.1);
    }

    i {
      font-size: 16px;
    }
  }

  // 主体内容
  .wrapper {
    width: 450px;

    // 图层没有时的提示
    .tip {
      width: 100%;
      min-height: 200px;
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 25px;
      color: #409eff;
    }
  }

  .el-tabs__header {
    min-width: 7.25rem;
  }

  // 分页器
  .el-pagination {
    white-space: unset;

    button,
    span:not([class*="suffix"]) {
      margin-bottom: 0.625rem;
    }
  }
}

.listWindow {
  transform: translate(500px);
}

// 列表窗展示
.listWindow_on {
  transform: translate(0);
}

#mapViewReductionButton {
  position: absolute;
  left: -80px;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: #4c4c4c;
  border: 2px solid #4c4c4c;

  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;

  z-index: 100;

  &:hover {
    border: 2px solid #409eff;
    background: white;

    i {
      color: #409eff;
    }
  }

  i {
    font-size: 35px;
    color: #fff;
  }
}

#mapBox {
  height: 100%;
}

.button-bar {
  position: fixed;
  top: 100px;
  right: 0;
  padding: 2em;
  width: 200px;
  background-color: #33f;
}

.tool-box {
  padding: 5px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
  position: absolute;
  top: 80px;
  right: 20px;
  background-color: #fff;
  line-height: 44px;
  color: #4c4c4c;
}

.tool-box a {
  color: #4c4c4c;
}

// baseBox控制面板单元格样式
.el-table {
  .el-table__row {
    cursor: pointer;

    td {
      padding: 0.125rem 0;
    }
  }
}

#app .el-table--small {
  th {
    padding: 0;
  }
}

// 表格单元格样式
.ivu-table-wrapper .ivu-table td,
.ivu-table-wrapper .ivu-table th {
  cursor: pointer;
  padding: 0.4rem 0;
  height: auto;
}

svg {
  // transition: 1s;
}

.topNav .el-switch__label,
.topNav .el-switch__label.is-active {
  color: white;
}

.ivu-table-wrapper .ivu-table-small td {
  height: 1.5625rem;
}

// 分割块
.divider {
  height: 0.625rem;
}

// 分页
.pagination {
  height: 23px;
  line-height: 23px;
}

// tips提示框
.esriPopupWrapper {
  // 头部
  .sizer {
    .titlePane {
      background: rgb(83, 167, 255);
      border-radius: 0;
      line-height: 30px;

      .title {
        background: rgb(83, 167, 255);
        border-bottom: 0;
      }

      // 最大化按钮
      .titleButton.maximize {
        top: 10px;
        display: none;
      }

      // 关闭按钮
      .titleButton {
        top: 6px;
        display: none;
      }
    }
  }
}

body {
  // iview选择框下拉最大高度
  .ivu-select-dropdown {
    max-height: 400px;
  }

  //
  .el-table__empty-block {
    text-align: left;
  }
}

// 滑框样式
.ivu-drawer {
  .el-tabs__content {
    overflow: unset;
  }
}

/* -------------图片放大(开始)----------------- */

/* 触发弹窗图片的样式 */
.Img {
  // border-radius: 5px;
  cursor: pointer;
  transition: 0.3s;
}

.Img:hover {
  opacity: 0.9;
  // filter: brightness(1.5);
}

/* 弹窗背景 */
.imgModal {
  display: none; /* Hidden by default */
  position: fixed; /* Stay in place */
  z-index: 9999; /* Sit on top */
  padding-top: 100px; /* Location of the box */
  left: 0;
  top: 0;
  width: 100%; /* Full width */
  height: 100%; /* Full height */
  overflow: auto; /* Enable scroll if needed */
  background-color: rgb(0, 0, 0); /* Fallback color */
  background-color: rgba(0, 0, 0, 0.9); /* Black w/ opacity */
}

/* 图片 */
.modal-content {
  margin: auto;
  display: block;
  // width: 45%;
  max-width: 65%;
  // height: 90%;
  max-height: 90%;
}

/* 添加动画 */
.modal-content,
#caption {
  -webkit-animation-name: zoom;
  -webkit-animation-duration: 0.6s;
  animation-name: zoom;
  animation-duration: 0.6s;
}

/* 工具栏 */
#toolbar {
  width: 260px;
  height: 60px;
  position: absolute;
  right: 165px;
  bottom: 83px;
  font-size: 45px;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  border-radius: 20px;
  z-index: 10;

  i {
    margin: 0 5px 0 0;
    transition: 0.3s;
    cursor: pointer;

    &:hover {
      transform: scale(1.1);
    }
  }
}

#toolbar > .el-button {
  border-radius: 0px !important;
}

@-webkit-keyframes zoom {
  from {
    -webkit-transform: scale(0);
  }
  to {
    -webkit-transform: scale(1);
  }
}

@keyframes zoom {
  from {
    transform: scale(0);
  }
  to {
    transform: scale(1);
  }
}

/* 关闭按钮 */
.close {
  position: absolute;
  top: 15px;
  right: 35px;
  color: #f1f1f1;
  font-size: 40px;
  font-weight: bold;
  transition: 0.3s;
}

.close:hover,
.close:focus {
  color: #bbb;
  text-decoration: none;
  cursor: pointer;
}

.el-carousel {
  .el-carousel__container {
    height: 100%;

    .el-carousel__item {
      display: flex;
      align-items: center;
      justify-content: center;
    }
  }
}

/* 小屏幕中图片宽度为 100% */
@media only screen and (max-width: 600px) {
  .modal-content {
    width: 100%;
  }
}

/* -------------图片放大(结束)----------------- */

.crp {
  cursor: pointer;
}

/* -------------消息窗口和控制器(开始)----------------- */

#Message_Window {
  z-index: 999;
  position: absolute;
  width: 300px;
  height: 362px;
  background: #fff;
  bottom: 50px;
  right: 30px;
  // border-radius: 10%;
  /* box-sizing: border-box; */
  // border: #52a7fe 2px solid;

  overflow: auto;

  .title {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1rem;
    height: 1.5625rem;
    border-bottom: 0;
    background: #52a7fe;
    color: white;
    overflow: hidden;
    position: relative;

    .window_tool {
      position: absolute;
      display: inline-block;
      right: 0;

      i {
        margin-right: 5px;
        cursor: pointer;

        &:hover {
          transform: scale(1.1);
        }
      }
    }
  }

  .content {
    list-style: none;
    height: 262px;
    overflow: auto;
    background: rgb(246, 247, 250);

    li {
      display: flex;
      height: 40px;
      align-items: center;
      padding-left: 0.9375rem;
      font-size: 0.875rem;

      cursor: pointer;

      &:hover {
        background: #fff;
      }
    }
  }
}

#Message_Window_Controller {
  position: absolute;
  bottom: 15px;
  right: 15px;
  border: #52a7fe 2px solid;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  cursor: pointer;
  box-sizing: content-box;
  display: flex;
  justify-content: center;
  align-items: center;

  .text {
    width: 40px;
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  &:hover {
    transform: scale(1.1);
    filter: brightness(1.1);
  }
}

/* -------------消息窗口和控制器(结束)----------------- */

/* -------------过渡动画(开始)----------------- */

.Messag-enter-active,
.Messag-leave-active {
  transition: 0.4s;
}

.Messag-enter,
.Messag-leave-to {
  opacity: 0;
  transform: translateX(-100%);
}

.Messag-complete-leave-active {
  // position: absolute;
}

.el-date-editor .el-range__close-icon,
.el-picker-panel__footer .el-button--text {
  display: none !important;
}

/* -------------过渡动画(结束)----------------- */





.ivu-divider-horizontal{
  margin-bottom: 0 !important;
}
.borsLine{
  text-align: left;height: 40px;line-height: 40px
}
