      import FilterMethods from "@/assets/commonJS/FilterMethods";
    import GetDataMethods from "@/assets/commonJS/GetDataMethods";
	export default {
        data(){
            return{
                loading:false,
                date:'',
                data:[],
                form:{
                    adressList:[],
                    qudaoList:[],
                    model_adress:'',
                    zdlx:'',
                    model_qudao:'',
                    date:'',
                    searchmsg:'',
                    xzqh:'',
                    qd:'',
                },
                cllx: {
                    Matieral_Type: [],
                    Field: '',
                },
                csjg: {
                    FlumBody_Type: [],
                    Field: '',
                },
                list_input:{
                    total:100,
                    pagesize:50,
                    pagesizeopts:[10,20,50,75,100,200],
                    current:1,
                },
            }
        },
                // 引入过滤方法到此组件
        mixins: [FilterMethods,GetDataMethods],
        mounted(){
            this.Reload();
        },
        methods:{
            err(){
              // console.log("showDrawer",this.$showDrawer);
                this.$Message.warning('该功能还在开发中...');
            },
            search(){
              if (this.form.searchmsg != '') {
                this.searchs = '&Flum_Name=like,'+this.form.searchmsg;
              }else{
                this.searchs = '';
              }
                this.Reload();
            },
            indexMethod(index){
                return index + 1 + (this.list_input.pagesize*(this.list_input.current-1));
            },
            timechange(date){
                console.log(date);
                this.date = date;
            },
            Reload(){
                // this.loading = true;
                this.axios.get('/guanqu/admin/WRP_IrrBTFlume?_page_size='+this.list_input.pagesize+'&_page='+this.list_input.current+this.searchs,{params:{Matieral_Type: this.cllx.Field, FlumBody_Type: this.csjg.Field,}}).then((res)=>{
                    console.log(res);
                    this.loading = false;
                    this.data = res.data.rows;
                    this.list_input.total = res.data.total;
                    for (var i = 0;i<this.data.length ; i++) {
                      this.data[i].D_FL = this.Z_Filter(this.data[i].D_FL,3);
                      this.data[i].ADD_FL = this.Z_Filter(this.data[i].ADD_FL,3);
                      this.data[i].IN_Z = this.Z_Filter(this.data[i].IN_Z);
                      this.data[i].OUT_Z = this.Z_Filter(this.data[i].OUT_Z);
                    }
                });
                this.getTableData_WRP_IrrBTCanalSystem(data => {
                  this.form.qudaoList = data;
                });
                this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=Matieral_Type').then((res)=>{
                  this.cllx.Matieral_Type = res.data.rows;
                });
                this.axios.get('/guanqu/admin/WRP_FieldInfo?FieldID=FlumBody_Type').then((res)=>{
                  this.csjg.FlumBody_Type = res.data.rows;
                });
            },
            // 处理页码切换
            CurrentChange(index) {
                // console.log(index);
              this.list_input.current = index;
              this.Reload();
            },
            // 处理每页显示条数切换
            PagesizeChange(pagesize) {
                // console.log(pagesize)
              this.list_input.pagesize = pagesize;
              this.Reload();
            },
        },
    }
