import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
import App from "@/App.vue";

export default {
    data() {
        return {
            yujingdata: [],
            loading: false,
            dialog_detail_showing: false,
            zdlxlist: [
                {
                    value: '1',
                    label: '闸门'
                },
                {
                    value: '2',
                    label: '阀门'
                },
            ],
            Tables: {
                title: '',
                total: '',
                data: [],
            },
            form: {
                adressList: [],
                qudaoList: [],
                model_adress: '',
                zdlx: '',
                model_qudao: '',
                date: '',
                searchmsg: '',
                xzqh: '',
                qd: '',
                field: '',
            },
            Tables: {
                title: '',
                total: '',
                data: [],
            },
            searchs: '',
            search1: '&STNM=like,',
            search2: '&TM=',
            search3: '&',
            search4: '&',
            search5: '&',
            timesearch: '',
            data: [],
            nodata: [],
            list_input: {
                total: 100,
                pagesize: 50,
                pagesizeopts: [10, 20, 50, 75, 100, 200],
                current: 1,
            },
        }
    },
    mounted() {
        var date = new Date();
        var Month = date.getMonth() + 1;
        this.form.date = date.getFullYear() + '-' + Month + '-' + date.getDate();
        this.Reload();
    },
    // 引入过滤方法到此组件
    mixins: [FilterMethods, GetDataMethods],
    methods: {
        sort_change(column) {
            console.log(column);
            if (column.order == 'ascending') {
                this.form.field = column.prop;
                this.Reload();
            }
            if (column.order == 'descending') {
                this.form.field = column.prop + ' desc';
                this.Reload();
            }
            if (column.order == null) {
                this.form.field = '';
                this.Reload();
            }
        },
        optable() {
            this.dialog_detail_showing = true;
        },
        cellclick(row, column, cell, event) {
            console.log('327',row);
            var evt = new Object();
            evt.graphic = new Object();
            // 给row对象添加预警信息
            this.yujingdata.forEach(item => {
                // 判断是否点击那一行的预警信息
               if(item.rowinfo.STCD === row.STCD){
                   row.YUJING_EZ = item.rowinfo.YUJING_EZ;
                   row.YUJING_JYEZ = item.rowinfo.YUJING_JYEZ;
                   row.YUJING_JYQ = item.rowinfo.YUJING_JYQ;
                   row.YUJING_JYSZ = item.rowinfo.YUJING_JYSZ;
                   row.YUJING_Q = item.rowinfo.YUJING_Q;
                   row.YUJING_SZ = item.rowinfo.YUJING_SZ;
               }
            });
            evt.graphic.attributes = row;
            evt.graphic.attributes.itype = "zhafashuiqing";
            evt.graphic.attributes.rowinfo = JSON.parse(JSON.stringify(row));
            evt.graphic.attributes.STNM = evt.graphic.attributes.STNM;
            evt.graphic.attributes.STCD = evt.graphic.attributes.STCD;
            evt.graphic.attributes.rowinfo.STNM = evt.graphic.attributes.rowinfo.STNM;
            evt.graphic.attributes.rowinfo.STCD = evt.graphic.attributes.rowinfo.STCD;
            evt.graphic.attributes.rowinfo.tableType = {
                zfsq: "dayTable"
            };
            console.log(row);
            var v = new Object;
            v.itype = "zhafashuiqing";
            console.log("evt", evt);
            this.$App.showDrawer(evt, v);
        },
        err() {
            this.$Message.warning('该功能还在开发中...');
        },
        timechange(date) {
            console.log(date);
            this.form.date = date;
            this.Reload();
        },
        indexMethod(index) {
            return index + 1 + (this.list_input.pagesize * (this.list_input.current - 1));
        },
        search() {
            if (this.form.searchmsg !== '') {
                this.searchs = '&STNM=' + this.form.searchmsg;
            } else {
                this.searchs = '';
            }
            this.Reload();
        },
        XZQHsearch() {
            console.log(this.form.model_adress);
            this.Reload();
        },
        QDsearch() {
            console.log(this.form.model_qudao);
            this.Reload();
        },
        TYPEsearch() {
            console.log(this.form.zdlx);
            this.Reload();
        },
        Reload() {
            this.loading = true;
            if (this.form.model_adress.length === 0) {
                this.form.xzqh = '';
            }
            if (this.form.model_adress.length === 1) {
                var str1 = this.form.model_adress[0];
                str1 = str1.substring(0, 6);
                this.form.xzqh = str1;
            }
            if (this.form.model_adress.length === 2) {
                var str2 = this.form.model_adress[1];
                str2 = str2.substring(0, 9);
                this.form.xzqh = str2;
            }
            if (this.form.model_adress.length === 3) {
                var str3 = this.form.model_adress[2];
                str3 = str3.substring(0, 12);
                this.form.xzqh = str3;
            }
            if (this.form.model_qudao.length === 0) {
                this.form.qd = '';
            }
            if (this.form.model_qudao.length == 1) {
                // var str4 = this.form.model_qudao[0];
                // str4 = str4.substring(0, 5);
                this.form.qd = this.form.model_qudao[0];
            }
            if (this.form.model_qudao.length == 2) {
                // var str5 = this.form.model_qudao[1];
                // str5 = str5.substring(0, 9);
                this.form.qd = this.form.model_qudao[1];
            }

            this.axios.get('/guanqu/zhafashuiqing/lishi_ribiao?' + this.searchs, {
                params: {
                    TM: this.form.date,
                    ADDVCD: this.form.xzqh,
                    Canal_Code: this.form.qd,
                    CTYPE: this.form.zdlx
                }
            }).then((res) => {
                console.log(res);
                this.loading = false;


                this.data = res.data.list.map((val, index) => {
                    // debugger;
                    for (let key in val) {
                        if (val[key] == 0) {
                            val[key] = ""
                        } else if (/Z$/.test(key)) {
                            val[key] = this.Float_Filter(val[key], 2)
                        } else if (/Q$/.test(key)) {
                            val[key] = this.Float_Filter(val[key], 3)
                        } else if (key.indexOf('小时累计水量') !== -1) {
                            val[key] = `${val[key]}`;
                            let oFl = val[key].split('.');
                            if (oFl[1] === undefined) {
                                val[key] = `${val[key]}.000`
                            } else if (oFl[1].length === 1) {
                                val[key] = `${val[key]}00`
                            } else if (oFl[1].length === 2) {
                                val[key] = `${val[key]}0`
                            } else if (oFl[1].length === 3) {
                                val[key] = val[key]
                            } else {
                                val[key] = val[key]
                            }
                        }
                    }
                    console.log(val)
                    return val
                });
                this.Tables.total = res.data.total;
                this.list_input.total = res.data.total;
                this.Tables.title = this.data.TM;
                for (var i = 0; i < this.data.length; i++) {
                    this.data[i].DA_UPZ = this.Z_Filter(this.data[i].DA_UPZ);
                    this.data[i].DA_DWZ = this.Z_Filter(this.data[i].DA_DWZ);
                    this.data[i].DA_Q = this.Z_Filter(this.data[i].DA_Q, 3);
                    this.data[i].DWQ = this.Z_Filter(this.data[i].DWQ, 3);
                    if (this.data[i].ctype === '1') {
                        this.data[i].ctype = '闸门';
                    }
                    if (this.data[i].ctype === '2') {
                        this.data[i].ctype = '阀门';
                    }
                }

            });
            this.getTableData_WRP_AD_B(data => {
                this.form.adressList = data;
            });
            // 获取输排水渠道数据,然后设置渠道选择框选项
            this.getTableData_WRP_IrrBTCanalSystem(data => {
                this.form.qudaoList = data;
            });
            this.axios.get('/guanqu/zhafashuiqing/lishi_ribiao?' + this.searchs, {
                params: {
                    TM: this.form.date,
                    ADDVCD: this.form.xzqh,
                    Canal_Code: this.form.qd,
                    CTYPE: this.form.zdlx
                }
            }).then((res) => {
                console.log(res);
                this.loading = false;
                this.Tables.data = res.data.list;
                for (var i = 0; i < this.Tables.data.length; i++) {
                    this.Tables.data[i].DA_UPZ = this.Z_Filter(this.Tables.data[i].DA_UPZ);
                    this.Tables.data[i].DA_DWZ = this.Z_Filter(this.Tables.data[i].DA_DWZ);
                    this.Tables.data[i].DWQ = this.Z_Filter(this.Tables.data[i].DWQ, 3);
                    this.Tables.data[i].DA_Q = this.Z_Filter(this.Tables.data[i].DA_Q, 3);
                }
            });

            // 取预警信息
            this.axios.get('/guanqu/zhafashuiqing/gis').then(res => {
                console.log('++++++++++++', res);
                this.yujingdata = res.data.features;
            });
        },

        // 处理页码切换
        CurrentChange(index) {
            // console.log(index);
            this.list_input.current = index;
            this.Reload();
        },
        // 处理每页显示条数切换
        PagesizeChange(pagesize) {
            // console.log(pagesize)
            this.list_input.pagesize = pagesize;
            this.Reload();
        },
    },
}
