import TipsYUNXINGGONGKUANG from "@/components/BaseBox/Tips/Tips-yunxinggongkuang.vue";
import FilterMethods from "@/assets/commonJS/FilterMethods";
import GetDataMethods from "@/assets/commonJS/GetDataMethods";
export default {
  props: {
    featrue: {
      type: Object
    },
    search_FeatrueLayer: {
      type: Function
    }
  },
  components: {
    TipsYUNXINGGONGKUANG
  },
  data() {
    return {
      // 折叠面板对象
      collapse: {
        activeName: "",
        list: []
      },
      // 地图对象
      map: null,

      // 该图层对象
      JsonFlayer: null,
      // 文字标注图层
      TextGraphicsLayers: [],
      // 提示 展示
      tip: {
        info_tip: null,
        show_tip: false
      },
      // 表单数据
      form: {
        // 搜索框
        search_str: "",
        // 地址选择列表
        adressList: [],
        // 渠道选择列表
        qudaoList: [],
        social: ["site"],
        // 状态选择列表
        statusList: [
          {
            value: 1,
            label: "正常"
          },
          {
            value: 2,
            label: "异常"
          },
          {
            value: 3,
            label: "电压异常"
          },
          {
            value: 4,
            label: "通讯异常"
          }
        ],
        // 多选框列表
        checkBoxList: [
          {
            value: "site",
            size: "small",
            span: 5,
            // iconType: "logo-facebook",
            title: "站点"
          },
          {
            value: "STNM",
            size: "small",
            span: 6,
            // iconType: "logo-facebook",
            title: "站名"
          },
          {
            value: "vol",
            size: "small",
            span: 6,
            // iconType: "logo-facebook",
            title: "电压值"
          },
          {
            value: "CS",
            size: "small",
            span: 7,
            // iconType: "logo-facebook",
            title: "通讯状态"
          }
        ],
        // 地址选择
        model_adress: "",
        // 渠道
        model_qudao: "",
        // 状态
        model_status: ""
      },
      // 表格数据
      table: {
        // 表头设置
        columns: [
          {
            title: "",
            key: "index",
            width: 45,
            align: "center",
            fixed: "left"
          },
          {
            title: "站名",
            key: "STNM",
            width: 120,
            align: "center",
            fixed: "left",
            sortable: "custom"
          },
          {
            title: "时间",
            key: "tm",
            width: 120,
            align: "center",
            sortable: "custom"
          },
          // {
          //   title: "电压",
          //   key: "vol",
          //   width: 100,
          //   align: "center",
          //   sortable: "custom"
          // }
        ],
        // 表体内容
        Rows: [],
        // 表格是否加载中
        loading: false,
        // 当前页
        currentPage: 1,
        // 每页显示数量
        pageSizes: 20,
        // 表体高度
        height: 300
      }
    };
  },
  // 引入过滤方法到此组件
  mixins: [FilterMethods, GetDataMethods],
  methods: {
    // 表格每一行点击事件
    cellClick(item) {
      var evt = new Object();
      evt.graphic = new Object();
      evt.graphic.attributes = item;
      evt.graphic.attributes.rowinfo = item;
      var v = new Object();
      v.itype = "yunxinggongkuang";

        console.log(item);

        // 设置中心缩放
      this.setMapCenterandZoom(item.LGTD, item.LTTD);
      // 取消所有站点的闪烁
      this.$App.GraphicsLayer_Selection_clear();
      // 对应站点闪烁
      this.$App.add_Graphic_To_GraphicsLayer_Selection(item);
      // 展示抽屉详情框
      this.$App.showDrawer(evt, v);
    },
    // 展示提示框
    showTips(item) {
      // 隐藏之前提示框
      this.hideTips();
      // 延时200毫秒
      setTimeout(() => {
        var div = `<div>
        <div class="tip_row">
                <span class="tip_col_5">时间：</span>
                <span class="tip_col_7">${item.rowinfo.tm}</span>
                <span class="tip_col_5">通讯状态：</span>
                <span class="tip_col_7">${
                  item.rowinfo.CS}</span>
                <span class="tip_col_5">电压：</span>
                <span class="tip_col_7">${this.$FilterData.Float_Filter(
                  item.rowinfo.vol,1
                )} v</span>
              </div>
              <div class="divider"></div>
              <div class="tip_row">
                <span class="tip_col_5">电压范围：</span>
                <span class="tip_col_7" id="${item.STCD}_VOLMAX_VOLMIN">
                </span>
                <span class="tip_col_5">站址：</span>
                <span class="tip_col_7">
                ${item.rowinfo.STLC}
                </span>
              </div>
              <div class="divider"></div>
        <div id="${
          item.STCD
        }" style="width: 550px;height:290px;margin:auto"></div>
      </div>`;
        this.map.infoWindow.resize(570, 650); // 提示框大小
        this.map.infoWindow.setTitle(item.STNM); // 提示题目
        this.map.infoWindow.setContent(div); // 提示内容


                //获取坐标
                var x=Number(item.LGTD);
                var y=Number(item.LTTD);


                //如果basemap使用ArcGIS的就需要
                //转换坐标系
                x = (x / 180.0) * 20037508.34
                if (y > 85.05112) y = 85.05112;
                if (y < -85.05112) y = -85.05112;
                y = (Math.PI / 180.0) * y;
                var tmp = Math.PI / 4.0 + y / 2.0;
                y = 20037508.34 * Math.log(Math.tan(tmp)) / Math.PI;

                //位置点对象
                var zoompoint = {
                    //spatialReference: {wkid: 4326},
                    spatialReference: this.map.spatialReference,
                    x: x,
                    y: y
                };


        this.map.infoWindow.show(zoompoint); // 提示位置
        // 如果事件类型不是鼠标经过，则
        if (item.eventType != "mouse-over") {
          // 设置中心和缩放 (接受经纬度，和缩放比例)
          this.setMapCenterandZoom(Number(item.LGTD) + 0.1, item.LTTD);
        }
        var body = {
          STCD: item.STCD,
          _page_size: 999999
        };

          body.STCD = item.STCD;
          delete body.TM;
          delete body._page_size;

          // 正常电压范围
          this.$GetData.Survey_Around_YXGK(null,body,false,data => {

              const around = document.getElementById(`${item.STCD}_VOLMAX_VOLMIN`)
              let dataVol = data.data[0],
                  volMin = dataVol.VOLMIN,
                  volMax = dataVol.VOLMAX
              around.innerHTML = `${volMin}v - ${volMax}v`
          })
        let obj = JSON.parse(JSON.stringify(body))

        let setDate = (dateType) => {
          let oDate = new Date();
          console.log(oDate);
          let YY = oDate.getFullYear();
          let MM = (oDate.getMonth() + 1) < 10? `0${(oDate.getMonth() + 1)}`: (oDate.getMonth() + 1);
          let DD = oDate.getDate() < 10? `0${oDate.getDate()}` : oDate.getDate();
          let HH = oDate.getHours() < 10? `0${oDate.getHours()}` : oDate.getHours();
          let mm = oDate.getMinutes() < 10? `0${oDate.getMinutes()}` : oDate.getHours();
          let SS = oDate.getSeconds() < 10? `0${oDate.getSeconds()}` : oDate.getSeconds();

          if(dateType === 'now'){
            return `${YY}-${MM}-${DD} ${HH}:${mm}:${SS}`
          }else {
            return `${YY}-${MM}-${DD} 00:00:00`
          }

        };

        obj.Time_max = setDate('now');
        obj.Time_min = setDate('0');
        console.log(obj);
        setDate(null)
        this.$GetData.Survey_History_YXGK(
          "historyTable",
          obj,
          {
            default: true
          },
          data => {
            this.createChart_zhuangtaishuju(
              item.STCD,
              "zhuangtaishuju",
              data.data
            );
            // this.$GetData.Survey_History_YXGK(
            //   "auxiliaryTable",
            //   {
            //     VOLTYPE: data.data[0].VOLTYPE
            //   },
            //   {
            //     default: true
            //   },
            //   data => {
            //     var data = data.data[0];
            //     if (data) {
            //       var div = document.getElementById(
            //         `${item.STCD}_VOLMAX_VOLMIN`
            //       );

            //       var VOLMAX_VOLMIN = `${data.VOLMIN} ~ ${data.VOLMAX}`;

            //       div.innerHTML = `<span>${VOLMAX_VOLMIN}</span>`;
            //     }
            //   }
            // );
          }
        );
      }, 200);

    },
    // 制图
    createChart_zhuangtaishuju(id, tableType, data) {
      var ele = document.getElementById(id);

      if (data.length > 0) {
        var echartData = this.$App.transform_YXGK_data_into_ehart_data(
          data,
          tableType
        ); // 渠道水情历史统计表数据 转 ehart图形用数据 返回一个对象, 对象里分别装 Y1轴对象 Y2轴对象 X轴对象

        var myChart = this.$echarts.init(ele);

        myChart.setOption({
          title: {
            text: echartData.chartName
          },
          grid: {
            x: '20%'
          },
          tooltip: {
            trigger: "axis"
          },
          // legend: {
          //   data: [echartData.y1.name],
          //   y: "bottom"
          // },
          calculable: true,
          animation: false,
          xAxis: [
            {
              type: "category",
              boundaryGap: false,
              data: echartData.x.list,
              axisLabel: {
                rotate: 15,
              },
            }
          ],
          yAxis: [
            {
              name: `${echartData.y1.name} V`,
              type: "value",
              axisLabel: {
                formatter: "{value} "
              },
              max: echartData.y1.max,
              min: echartData.y1.min
            }
          ],
            axis: {
                axisLabel: {
                    interval: 'auto'
                },
                axisTick: {
                    interval: 'auto'
                }
            },
          series: [
            {
              name: echartData.y1.name,
              type: "line",
              data: echartData.y1.list
            }
          ]
        });
      } else {
        ele.style.display = "flex";
        ele.style.alignItems = "center";
        ele.style.justifyContent = "center";
        ele.innerHTML = "暂无今天数据";
      }
    },


    // 设置中心和缩放 (接受经纬度，和缩放比例)
    setMapCenterandZoom(LGTD, LTTD, Zoom) {
      //koen 20190929
      //暂时不允许点击，防止缩到全球地图的bug
      //this.map.centerAndZoom([Number(LGTD), Number(LTTD)], Zoom || 1);
    },
    // 隐藏提示
    hideTips() {
      this.map.infoWindow.hide();
      this.tip.show_tip = false; // 关闭提示框组件
    },
    // 表格加载
    tableLoading() {
      this.table.loading = true;
    },
    // 表格加载取消
    cancelTableLoading() {
      this.table.loading = false;
    },
    // 加载表格数据
    loadTableData(data) {
      // 加载站点数据
      this.table.Rows = data;
    },
    getDataFromEnd(url, callback) {
      // 获取表格数据 `/guanqu/gis/details?ID=${this.info.ID}`
      this.axios.get(url).then(res => {
        if (typeof callback == "function") {
          callback(res);
        }
      });
    },
    // 查询
    search() {
      var body = new Object();
      // 如果输入框有内容，添加站名过滤字段
      if (this.form.search_str.length > 0) {
        body["STNM"] = `${this.form.search_str}`;
      }
      // 如果地址选择框有内容，添加行政区划过滤字段
      if (this.form.model_adress && this.form.model_adress.length != 0) {
        body["ADDVCD"] = `${this.$App.ADDVCD_Array_Filter(
          this.form.model_adress
        )}`;
      }
      // 如果渠道选择框有内容，添加渠道过滤字段
      if (this.form.model_qudao && this.form.model_qudao.length != 0) {
        body["Canal_Code"] = `${this.$App.Canal_Code_Array_Filter(
          this.form.model_qudao
        )}`;
      }

      if (this.form.model_status && this.form.model_status.length != 0) {
        body["_stat"] = this.form.model_status;
      }

      this.tableLoading(); // 表格加载中
      this.search_FeatrueLayer(
        // 9, // 图层type类型
        "yunxinggongkuang", // 图层type类型
        body,
        this.featrue,
        newFeatureLayerOBJ => {
          this.table.currentPage = 1;
          this.baseBox_init();
          this.cancelTableLoading(); //取消表格加载
          this.hideTips();
        }
      );
    },
    // 给图层对象添加方法
    addEventToJsonFlayer(JsonFlayer) {
      //当鼠标悬停在元素上
      JsonFlayer.on("mouse-over", evt => {
        var attributes = evt.graphic.attributes;
        attributes.eventType = "mouse-over";
        this.showTips(attributes);
        // 给地图对象添加方法
        this.addEventToMap();
      });

      //当鼠标悬停在元素上
      JsonFlayer.on("mouse-out", evt => {
        this.hideTips();
      });
    },
    // 给地图对象添加方法
    addEventToMap() {
      this.map.on("click", () => {
        // 隐藏提示
        this.hideTips();
      });
    },
    // 处理页码切换
    handleCurrentChange(index) {
      this.table.currentPage = index;
    },
    // 处理每页显示条数切换
    handleSizeChange(pageSizes) {
      this.table.pageSizes = pageSizes;
      this.table.currentPage = 1;
    },
    // 分页过滤
    pageFilter(currentPage, pageSizes) {
      var _currentPage = currentPage || this.table.currentPage;
      var _pageSizes = pageSizes || this.table.pageSizes;
      if (this.table.Rows_filter.length != 0) {
        // 截取数组
        var newList = this.table.Rows_filter.slice(
          (_currentPage - 1) * _pageSizes,
          _currentPage * _pageSizes
        );

        return newList;
      } else {
        return [];
      }
    },
    // 过滤table数据
    filterTableData() {
      this.table.Rows_filter = this.table.Rows.map((val, index) => {
        var val_clone = JSON.parse(JSON.stringify(val));
        // 序号
        val_clone.index = index + 1;
        // 时间过滤
        val_clone.TM = this.dateFilter(val_clone.TM);
        // 测报电压过滤
        val_clone.vol = this.$FilterData.Float_Filter(val_clone.vol,1 );
        // 测报通讯状态过滤
        // val_clone.CS = this.CS_Filter(val_clone.CS);
        return val_clone;
      });
    },
    // 选取一部分从父组件传来的数据作为表格的数据
    getTableDataFormParent() {
      var tableData = this.featrue.Rows.map(val => {
        return val.rowinfo;
      });

      return tableData;
    },
    // 获取图层对象,从父组件中
    getJsonFlayerFormParent() {
      this.JsonFlayer = this.featrue.LayerObject;
    },
    // 获取地图对象,从父组件中
    getMapFormParent() {
      this.map = this.featrue.map;
    },
    // 往featrue对象里添加操作TextGraphicsLayer的对象，里面有移除所有TextGraphicsLayer的方法，和根据多选框添加文字标注图层的方法
    addTextGraphicsLayerControllerToFeatrue() {
      this.featrue.TextGraphicsLayerController = new Object();
      this.featrue.TextGraphicsLayerController.removeAllTextGraphicsLayer = this.removeAllTextGraphicsLayer;
      this.featrue.TextGraphicsLayerController.addTextGraphicsLayerByCheckGroup = this.addTextGraphicsLayerByCheckGroup;
    },
    // 多选框标记勾选触发事件
    checkboxGroup_onChange(onCheck_Array) {
      this.removeAllTextGraphicsLayer(); // 清除所有文本标注图层
      this.addTextGraphicsLayerByCheckGroup(); // 根据多选框添加文字标注图层
    },
    // 添加文字标注图层
    addTextGraphicsLayer(options) {
      var textGraphicsLayer = new esri.layers.GraphicsLayer(); // 新增一个标注文本图层
      textGraphicsLayer.textType = options.textType;
      this.featrue.map.addLayer(textGraphicsLayer); // 给地图添加新增的标注文本图层
      var Rows = this.featrue.Rows;
      //创建textsymbol文本标注
      if (Rows.length > 0) {
        //动态读取json数据源结果集
        for (var i = 0; i < Rows.length; i++) {
          var Row = Rows[i];
          var point = new esri.geometry.Point(
            Row.geometry.x,
            Row.geometry.y,
            this.featrue.map.spatialReference
          );
          //定义文本symbol
          var textsymbol = new esri.symbol.TextSymbol(
            Row.rowinfo[options.textType]
          ) //动态设置文本值
            .setColor(new dojo.Color(options.color || [55, 55, 55])) //setColor设置文本颜色
            .setFont(
              new esri.symbol.Font(options.fontSize || "10pt") //setFont设置文本大小
                .setWeight(esri.symbol.Font.WEIGHT_BOLD)
            ) //setWeight设置文本粗体
            .setOffset(options.offset_x || 0, options.offset_y || -25); //设置偏移方向
          var graphic = new esri.Graphic(point, textsymbol);
          textGraphicsLayer.add(graphic);
        }
      }
      this.TextGraphicsLayers.push(textGraphicsLayer);
    },
    // 根据多选框添加文字标注图层
    addTextGraphicsLayerByCheckGroup() {
      var textTypeList;
      var indexOfSocial = this.form.social.indexOf("site");
      // 如果多选框里存在 site站点类型
      if (indexOfSocial !== -1) {
        this.featrue.LayerObject.show();
        this.featrue.LayerObject_yujing.show();
        textTypeList = this.form.social.filter(val => {
          if (val != "site") {
            return true;
          }
        });
      } else {
        this.featrue.LayerObject.hide();
        this.featrue.LayerObject_yujing.hide();
        textTypeList = this.form.social;
      }

      textTypeList.forEach((textType, index) => {
        var textGraphicsLayer = new esri.layers.GraphicsLayer(); // 新增一个图形图层
        textGraphicsLayer.textType = textType;
        this.featrue.map.addLayer(textGraphicsLayer); // 给地图添加新增的标注文本图层
        var Rows = this.featrue.Rows;
        //创建textsymbol文本标注
        if (Rows.length > 0) {
          //动态读取json数据源结果集
          for (var i = 0; i < Rows.length; i++) {
            var Row = Rows[i];
            var point = new esri.geometry.Point(
              Row.geometry.x,
              Row.geometry.y,
              this.featrue.map.spatialReference
            );
            console.log("textType",textType)

            var value = Row.rowinfo[textType];
            // 过滤
            switch (textType) {
              case "vol":
                var value = `${this.Z_Filter(Row.rowinfo[textType],1)}`;
                if (value == "") {
                  value = "";
                } else {
                  value += "V";
                }
                var color, vol = Row.rowinfo[textType];     // Row.rowinfo[textType]   [255, 0 , 0] : [55, 55, 55]
                if (vol >= 12 && vol <= 16) {
                  color = [55, 55, 55]
                }else {
                  color = [255, 0 , 0]
                }

                //定义文本symbol
                var textsymbol = new esri.symbol.TextSymbol(value) //动态设置文本值
                        .setColor(new dojo.Color(color)) //setColor设置文本颜色
                        .setFont(
                                new esri.symbol.Font("10pt") //setFont设置文本大小
                                        .setWeight(esri.symbol.Font.WEIGHT_BOLD)
                        ) //setWeight设置文本粗体
                        .setOffset(0, -(25 + index * 15)); //设置偏移方向

                break;
              case "CS":
                var value = Row.rowinfo[textType];
                if (value == "") {
                  value = "";
                }

                //定义文本symbol
                var textsymbol = new esri.symbol.TextSymbol(value) //动态设置文本值
                        .setColor(new dojo.Color(Row.rowinfo.CS == "异常" ? [255, 0 , 0] : [55, 55, 55])) //setColor设置文本颜色
                        .setFont(
                                new esri.symbol.Font("10pt") //setFont设置文本大小
                                        .setWeight(esri.symbol.Font.WEIGHT_BOLD)
                        ) //setWeight设置文本粗体
                        .setOffset(0, -(25 + index * 15)); //设置偏移方向
                break;
              case "STNM":
                var value = Row.rowinfo[textType];
                if (value == "") {
                  value = "";
                }

                //定义文本symbol
                var textsymbol = new esri.symbol.TextSymbol(value) //动态设置文本值
                        .setColor(new dojo.Color([55, 55, 55])) //setColor设置文本颜色
                        .setFont(
                                new esri.symbol.Font("10pt") //setFont设置文本大小
                                        .setWeight(esri.symbol.Font.WEIGHT_BOLD)
                        ) //setWeight设置文本粗体
                        .setOffset(0, -(25 + index * 15)); //设置偏移方向
                break;
            }


            var graphic = new esri.Graphic(point, textsymbol);
            textGraphicsLayer.add(graphic);
          }
        }
        this.TextGraphicsLayers.push(textGraphicsLayer);
      });
    },
    // 移除文字标注图层
    removeTextGraphicsLayer(textType) {
      // 找到对应的文字标注图层
      console.log(textType);
      for (let i = 0; i < this.TextGraphicsLayers.length; i++) {
        var val = this.TextGraphicsLayers[i];
        if (val.textType == textType) {
          this.featrue.map.removeLayer(val); // 移除对应的文字标注图层
          // val.hide();
          this.TextGraphicsLayers.splice(i, 1); // 从文字标注图层数组里去除对应的文字图层对象
        }
      }
    },
    // 移除所有文字标注图层
    removeAllTextGraphicsLayer() {
      for (let i = 0; i < this.TextGraphicsLayers.length; i++) {
        var val = this.TextGraphicsLayers[i];
        this.featrue.map.removeLayer(val); // 移除对应的文字标注图层
      }
      this.TextGraphicsLayers = [];
    },
    // 初始化baseBox
    baseBox_init() {
      // 获取地图对象,从父组件中
      this.getMapFormParent();
      // 获取图层对象,从父组件中
      this.getJsonFlayerFormParent();
      // 选取一部分从父组件传来的数据,将其加载为表格数据
      this.loadTableData(this.getTableDataFormParent());
      // 过滤table数据
      this.filterTableData();
      // 给图层对象添加方法
      this.addEventToJsonFlayer(this.JsonFlayer);

      // 获取行政区划数据,然后设置地址选择框选项
      this.getTableData_WRP_AD_B(data => {
        this.form.adressList = data;
        // data.map(val => {
        //   var newVal = new Object();
        //   newVal.value = val.AD_CD;
        //   newVal.label = val.AD_NM;
        //   return newVal;
        // });
      });

      // 获取输排水渠道数据,然后设置渠道选择框选项
      this.getTableData_WRP_IrrBTCanalSystem(data => {
        this.form.qudaoList = data;
        // data.map(val => {
        //   var newVal = new Object();
        //   newVal.value = val.Canal_Code;
        //   newVal.label = val.Canal_Name;
        //   return newVal;
        // });
      });

      // 多选框标记勾选触发事件
      // 1.清除所有文本标注图层
      // 2.根据多选框添加文字标注图层
      this.checkboxGroup_onChange();
      // 往featrue对象里添加操作TextGraphicsLayer的对象，里面有移除所有TextGraphicsLayer的方法，和根据多选框添加文字标注图层的方法
      this.addTextGraphicsLayerControllerToFeatrue();
    },
    // 排序
    sort_change(item) {
      var order = item.order,
        key = item.prop;

      this.filterTableData();
      var newList;
      if (order) {
        switch (order) {
          case "descending":
            if (key == "TM") {
              newList = this.table.Rows_filter.sort((a, b) => {
                var aTime = new Date(`20${a.TM}`).getTime(),
                  bTime = new Date(`20${b.TM}`).getTime();
                aTime = isNaN(aTime) ? 0 : aTime;
                bTime = isNaN(bTime) ? 0 : bTime;
                return bTime - aTime;
              });
            } else if (key == "CS") {
              newList = this.table.Rows_filter.sort((a, b) => {
                var a_cs = a["CS"] == "正常" ? 1 : 0,
                  b_cs = b["CS"] == "正常" ? 1 : 0;
                return b_cs - a_cs;
              });
            } else {
              newList = this.table.Rows_filter.sort((a, b) => {
                return Number(b[key]) - Number(a[key]);
              });
            }
            break;

          case "ascending":
            if (key == "TM") {
              newList = this.table.Rows_filter.sort((a, b) => {
                var aTime = new Date(`20${a.TM}`).getTime(),
                  bTime = new Date(`20${b.TM}`).getTime();
                aTime = isNaN(aTime) ? 0 : aTime;
                bTime = isNaN(bTime) ? 0 : bTime;
                return aTime - bTime;
              });
            } else if (key == "CS") {
              newList = this.table.Rows_filter.sort((a, b) => {
                var a_cs = a["CS"] == "正常" ? 1 : 0,
                  b_cs = b["CS"] == "正常" ? 1 : 0;
                return a_cs - b_cs;
              });
            } else {
              newList = this.table.Rows_filter.sort((a, b) => {
                return Number(a[key]) - Number(b[key]);
              });
            }
        }

        this.table.Rows_filter = newList;
      }
      this.$TableMethods.refreshCurrentChange(this.table, 1);
    }
  },
  created() {
    // 初始化baseBox
    this.baseBox_init();
    this.search();
    this.baseBox_Interval = setInterval(() => {
      this.search();
    },1000 * 60 * 5)
  },
  destroyed() {
    clearInterval(this.baseBox_Interval)
  }
};
