import EDIT from "@/table/table-xtqx/table-cdgl/edit.vue";

export default {
  props: {
    id: ""
  },
  data() {
    return {
      form: {},
      dialog_edit_showing:false,
      menu:{id:''},
    };
  },

  mounted() {
    console.log(this.id);
    this.Reload()
  },
  methods: {
    Reload(){
      this.axios.get('/guanqu/web/menus/'+this.id).then((res) => {
        this.form = res.data;
      });
    },
    del() {
      this.$confirm("您确定删除该菜单吗?", "", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          this.axios.delete('/guanqu/web/menus/'+this.id).then((res) => {
            // this.form = res.data;
            this.$emit("closewindows");
            this.$message({
              type: "success",
              message: "删除菜单成功!"
            });
          });
          
        })
        .catch(() => {
          this.$message({
            type: "error",
            message: "已取消删除"
          });
        });
    },
    openEdit(){
      this.menu.id = this.form.id;
      this.dialog_edit_showing = true;
    },
    close(){
      this.$emit("closewindows");
    },
  },
  computed: {},
  components: {EDIT}
};
